const fs = require('fs');
const path = require('path');

// .claude.json 파일 읽기
const configPath = path.join(__dirname, '.claude.json');
const backupPath = path.join(__dirname, '.claude.json.backup-utf8');

console.log('UTF-8 설정 추가 스크립트');
console.log('========================\n');

try {
    // 백업 생성
    const config = fs.readFileSync(configPath, 'utf8');
    fs.writeFileSync(backupPath, config);
    console.log('✅ 백업 생성: .claude.json.backup-utf8');

    // JSON 파싱
    const configObj = JSON.parse(config);

    // terminal 설정 추가/업데이트
    if (!configObj.terminal) {
        configObj.terminal = {};
    }
    configObj.terminal.encoding = 'utf-8';
    configObj.terminal.codepage = 65001;

    // environment 설정 추가
    if (!configObj.environment) {
        configObj.environment = {};
    }
    configObj.environment.LANG = 'ko_KR.UTF-8';
    configObj.environment.LC_ALL = 'ko_KR.UTF-8';

    // 저장
    fs.writeFileSync(configPath, JSON.stringify(configObj, null, 2));
    console.log('✅ UTF-8 설정 추가 완료!');
    console.log('\n추가된 설정:');
    console.log('- terminal.encoding: "utf-8"');
    console.log('- terminal.codepage: 65001');
    console.log('- environment.LANG: "ko_KR.UTF-8"');
    console.log('- environment.LC_ALL: "ko_KR.UTF-8"');

} catch (error) {
    console.error('❌ 오류 발생:', error.message);
    console.log('\n수동으로 다음 설정을 .claude.json에 추가하세요:');
    console.log(JSON.stringify({
        terminal: {
            encoding: "utf-8",
            codepage: 65001
        },
        environment: {
            LANG: "ko_KR.UTF-8",
            LC_ALL: "ko_KR.UTF-8"
        }
    }, null, 2));
}

