@echo off
REM Claude Code - Minimal Clean Version
REM Created: 2025-01-25

chcp 65001 >nul 2>&1

REM Set paths
set CLAUDE_HOME=%~dp0
set NODE_PATH=K:\tools\nodejs

REM Load API key from .env file
if exist "%CLAUDE_HOME%.env" (
    for /f "tokens=1,2 delims==" %%a in (%CLAUDE_HOME%.env) do (
        if "%%a"=="ANTHROPIC_API_KEY" set ANTHROPIC_API_KEY=%%b
    )
)

REM Check if API key exists
if "%ANTHROPIC_API_KEY%"=="" (
    echo ERROR: ANTHROPIC_API_KEY not found in .env file
    echo Please create .env file with your API key
    pause
    exit /b 1
)

REM Run Claude Code
cd /d "%CLAUDE_HOME%"
"%NODE_PATH%\node.exe" "%CLAUDE_HOME%node_modules\@anthropic-ai\claude-code\cli.js" %*

@echo off
echo ========================================
echo   Claude Code Clean Installation
echo ========================================
echo.

echo Installing Claude Code...
K:\tools\nodejs\npm.cmd install

echo.
echo ========================================
echo Installation complete!
echo.
echo Next steps:
echo 1. Edit .env file and add your ANTHROPIC_API_KEY
echo 2. Run claude.bat to start Claude Code
echo ========================================
pause
