const fs = require('fs');

console.log('Removing google-search from .claude.json...');

// Read config
const configPath = 'K:/PortableApps/Claude-Code/.claude.json';
const config = JSON.parse(fs.readFileSync(configPath, 'utf8'));

// Remove google-search from mcpServers
if (config.mcpServers && config.mcpServers['google-search']) {
    delete config.mcpServers['google-search'];
    console.log('✓ Removed google-search from mcpServers');

    // Write back
    fs.writeFileSync(configPath, JSON.stringify(config, null, 2));
    console.log('✓ Updated .claude.json');
} else {
    console.log('google-search not found in config');
}

console.log('Done!');

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

console.log('=== BASH ISSUE DEEP INVESTIGATION ===\n');

// 1. Check which bash is being called
console.log('[1] Checking bash executables...');
const bashPaths = [
    'C:\\Program Files\\Git\\bin\\bash.exe',
    'C:\\Program Files\\Git\\git-bash.exe',
    'K:\\PortableApps\\tools\\git\\bin\\bash.exe',
    'C:\\Windows\\System32\\bash.exe',  // WSL bash
    '/usr/bin/bash'  // Unix path (shouldn't exist on Windows)
];

bashPaths.forEach(bashPath => {
    if (fs.existsSync(bashPath)) {
        console.log(`✓ Found: ${bashPath}`);
        try {
            const version = execSync(`"${bashPath}" --version 2>&1`, {encoding: 'utf8'});
            console.log(`  Version: ${version.split('\n')[0]}`);
        } catch (e) {
            console.log(`  Error getting version`);
        }
    } else {
        console.log(`✗ Not found: ${bashPath}`);
    }
});

// 2. Check WSL
console.log('\n[2] Checking WSL...');
try {
    const wslStatus = execSync('wsl --status 2>&1', {encoding: 'utf8', timeout: 3000});
    console.log('WSL is installed:');
    console.log(wslStatus.substring(0, 200));
} catch (e) {
    console.log('WSL not installed or not accessible');
}

// 3. Check environment variables
console.log('\n[3] Environment variables...');
const envVars = ['PATH', 'SHELL', 'BASH_ENV', 'CLAUDE_CODE_GIT_BASH_PATH'];
envVars.forEach(env => {
    const value = process.env[env];
    if (value) {
        if (env === 'PATH') {
            const paths = value.split(';');
            console.log(`${env}: (${paths.length} paths)`);
            paths.forEach(p => {
                if (p.toLowerCase().includes('bash') || p.toLowerCase().includes('git') || p.toLowerCase().includes('wsl')) {
                    console.log(`  - ${p}`);
                }
            });
        } else {
            console.log(`${env}: ${value}`);
        }
    }
});

// 4. Check Claude Code's actual bash invocation
console.log('\n[4] Claude Code bash configuration...');
const claudeBatPath = 'K:\\PortableApps\\Claude-Code\\claude.bat';
if (fs.existsSync(claudeBatPath)) {
    const content = fs.readFileSync(claudeBatPath, 'utf8');
    const bashLine = content.match(/CLAUDE_CODE_GIT_BASH_PATH=(.+)/);
    if (bashLine) {
        console.log(`Configured: ${bashLine[1]}`);
        const configPath = bashLine[1].trim();
        console.log(`Exists: ${fs.existsSync(configPath) ? '✓' : '✗'}`);
    }
}

// 5. Check if WSL is interfering
console.log('\n[5] WSL interference check...');
try {
    // Check if wsl.exe exists
    const wslExe = 'C:\\Windows\\System32\\wsl.exe';
    if (fs.existsSync(wslExe)) {
        console.log('✓ WSL executable found');

        // Check WSL distributions
        try {
            const distros = execSync('wsl -l -v', {encoding: 'utf8', timeout: 3000});
            console.log('WSL distributions:');
            console.log(distros);
        } catch (e) {
            console.log('Cannot list WSL distributions');
        }
    } else {
        console.log('✗ WSL not found');
    }
} catch (e) {
    console.log('WSL check failed:', e.message);
}

// 6. Check Node.js child_process default shell
console.log('\n[6] Node.js shell configuration...');
console.log('process.env.COMSPEC:', process.env.COMSPEC || 'not set');
console.log('process.env.SHELL:', process.env.SHELL || 'not set');

// 7. Test different bash invocations
console.log('\n[7] Testing bash invocations...');
const testCommands = [
    { cmd: 'echo "test"', shell: true },
    { cmd: 'bash -c "echo test"', shell: true },
    { cmd: 'cmd /c "echo test"', shell: true }
];

testCommands.forEach(({cmd, shell}) => {
    try {
        const result = execSync(cmd, {shell, encoding: 'utf8', timeout: 2000});
        console.log(`✓ "${cmd}": ${result.trim()}`);
    } catch (e) {
        console.log(`✗ "${cmd}": Failed`);
    }
});

console.log('\n=== INVESTIGATION COMPLETE ===');

// Diagnosis
console.log('\n[DIAGNOSIS]');
if (fs.existsSync('C:\\Windows\\System32\\wsl.exe')) {
    console.log('⚠️ WSL is installed - may be interfering with Git Bash');
    console.log('   Claude Code might be using WSL bash instead of Git Bash');
}

if (!fs.existsSync('K:\\PortableApps\\tools\\git\\bin\\bash.exe')) {
    console.log('⚠️ K: drive Git Bash not found');
    console.log('   Need to verify Git installation on K: drive');
}

console.log('\n[SOLUTION]');
console.log('1. Disable WSL integration if not needed');
console.log('2. Ensure Git Bash is properly installed');
console.log('3. Set explicit bash path in environment');

@echo off
echo === RUNNING BASH ISSUE DETECTION ===
echo.
K:\PortableApps\tools\nodejs\node.exe K:\PortableApps\Claude-Code\DETECT-BASH-ISSUE.js
echo.
pause

@echo off
echo === DISABLE WSL BASH INTERFERENCE ===
echo.

REM Option 1: Rename WSL bash (needs admin)
echo [Option 1] Renaming WSL bash...
if exist "C:\Windows\System32\bash.exe" (
    ren "C:\Windows\System32\bash.exe" "bash-wsl.exe.disabled" 2>nul
    if %ERRORLEVEL% EQU 0 (
        echo SUCCESS: WSL bash disabled
    ) else (
        echo FAILED: Need admin rights
    )
) else (
    echo WSL bash not found
)

REM Option 2: Force Git Bash in PATH
echo.
echo [Option 2] Prioritizing Git Bash...
set PATH=K:\PortableApps\tools\git\bin;C:\Program Files\Git\bin;%PATH%
setx PATH "K:\PortableApps\tools\git\bin;C:\Program Files\Git\bin;%PATH%" >nul 2>&1

REM Option 3: Create wrapper script
echo.
echo [Option 3] Creating bash wrapper...
echo @echo off > K:\PortableApps\Claude-Code\bash.bat
echo K:\PortableApps\tools\git\bin\bash.exe %%* >> K:\PortableApps\Claude-Code\bash.bat
echo Created bash.bat wrapper

echo.
echo === SOLUTIONS APPLIED ===
echo Please restart Claude Code
pause

const fs = require('fs');
const path = require('path');

console.log('=== FIXING CLAUDE CODE BASH CONFIGURATION ===\n');

// 1. Check actual Claude Code CLI
const cliPath = 'K:\\PortableApps\\Claude-Code\\node_modules\\@anthropic-ai\\claude-code\\cli.js';
if (fs.existsSync(cliPath)) {
    console.log('[1] Found Claude Code CLI');

    // Read first 100 lines to check bash handling
    const content = fs.readFileSync(cliPath, 'utf8');
    const lines = content.split('\n').slice(0, 100);

    // Look for bash references
    let bashRefs = [];
    lines.forEach((line, i) => {
        if (line.includes('bash') || line.includes('/usr/bin')) {
            bashRefs.push(`Line ${i+1}: ${line.trim().substring(0, 80)}`);
        }
    });

    if (bashRefs.length > 0) {
        console.log('Found bash references in CLI:');
        bashRefs.forEach(ref => console.log(`  ${ref}`));
    }
}

// 2. Create override configuration
console.log('\n[2] Creating override configuration...');
const override = {
    "bash": {
        "path": "K:\\PortableApps\\tools\\git\\bin\\bash.exe",
        "args": ["-l"],
        "env": {
            "TMPDIR": "K:\\PortableApps\\Claude-Code\\temp",
            "HOME": "K:\\PortableApps\\Claude-Code"
        }
    }
};

fs.writeFileSync(
    'K:\\PortableApps\\Claude-Code\\bash-override.json',
    JSON.stringify(override, null, 2)
);
console.log('Created bash-override.json');

// 3. Create shell initialization script
console.log('\n[3] Creating shell init script...');
const initScript = `#!/bin/bash
# Claude Code Bash Initialization
export TMPDIR="K:/PortableApps/Claude-Code/temp"
export TMP="$TMPDIR"
export TEMP="$TMPDIR"

# Create temp if not exists
mkdir -p "$TMPDIR" 2>/dev/null

# Fix path separators
export PATH=$(echo $PATH | sed 's/\\\\/\\//g')

# Signal successful init
echo "Bash initialized for Claude Code"
`;

fs.writeFileSync('K:\\PortableApps\\Claude-Code\\init.sh', initScript);
console.log('Created init.sh');

// 4. Update .bashrc
console.log('\n[4] Updating .bashrc...');
const bashrc = `# Claude Code Bash Configuration
source K:/PortableApps/Claude-Code/init.sh
`;

fs.writeFileSync('K:\\PortableApps\\Claude-Code\\.bashrc', bashrc);
console.log('Updated .bashrc');

console.log('\n=== CONFIGURATION COMPLETE ===');
console.log('Next steps:');
console.log('1. Run RUN-DETECTION.bat to verify');
console.log('2. Restart Claude Code');
console.log('3. Test bash commands');

