if (fs.existsSync(bashPath)) {
    try {
        // Check $0 (how bash sees itself)
        const self = execSync(`"${bashPath}" -c "echo '$0'"`, {encoding: 'utf8'});
        console.log(`Bash $0: ${self.trim()}`);

        // Check pwd
        const pwd = execSync(`"${bashPath}" -c "pwd"`, {encoding: 'utf8'});
        console.log(`Initial pwd: ${pwd.trim()}`);

