#!/usr/bin/env node
/**
 * AUTO-CHECK.js - Bash 환경 자동 검증 및 치유 시스템
 * 
 * 기능:
 * 1. Bash 실행 가능 여부 체크
 * 2. 경로 변환 문제 진단
 * 3. 자동 수정 실행
 * 4. 결과 리포트 생성
 */

const { execSync, spawnSync } = require('child_process');
const fs = require('fs');
const path = require('path');

console.log('🔍 AUTO-CHECK: Bash 환경 자동 검증 시작...\n');

// 색상 코드
const colors = {
    red: '\x1b[31m',
    green: '\x1b[32m',
    yellow: '\x1b[33m',
    blue: '\x1b[34m',
    reset: '\x1b[0m'
};

// 검증 결과 저장
const report = {
    timestamp: new Date().toISOString(),
    checks: [],
    errors: [],
    fixes: [],
    status: 'CHECKING'
};

// 1. Bash 실행 가능 여부 체크
function checkBashExecutable() {
    console.log('1️⃣ Bash 실행 가능 여부 확인...');

    const bashPaths = [
        'K:\\PortableApps\\tools\\git\\bin\\bash.exe',
        'K:\\PortableApps\\tools\\git\\usr\\bin\\bash.exe',
        'C:\\Program Files\\Git\\bin\\bash.exe',
        'C:\\Program Files\\Git\\usr\\bin\\bash.exe'
    ];

    let foundBash = null;
    for (const bashPath of bashPaths) {
        if (fs.existsSync(bashPath)) {
            foundBash = bashPath;
            console.log(`${colors.green}✓${colors.reset} Bash 발견: ${bashPath}`);
            report.checks.push({
                name: 'Bash Executable',
                status: 'PASS',
                path: bashPath
            });
            break;
        }
    }

    if (!foundBash) {
        console.log(`${colors.red}✗${colors.reset} Bash를 찾을 수 없습니다!`);
        report.checks.push({
            name: 'Bash Executable',
            status: 'FAIL',
            error: 'No bash.exe found'
        });
        return null;
    }

    return foundBash;
}

// 2. 경로 변환 문제 진단
function diagnosePath(bashPath) {
    console.log('\n2️⃣ 경로 변환 문제 진단...');

    // K드라이브 접근 테스트
    const testCommands = [
        { cmd: 'pwd', desc: '현재 디렉토리' },
        { cmd: 'ls /k/ 2>/dev/null | head -n 1', desc: '/k/ 경로 접근' },
        { cmd: 'ls /cygdrive/k/ 2>/dev/null | head -n 1', desc: '/cygdrive/k/ 경로 접근' },
        { cmd: 'ls /mnt/k/ 2>/dev/null | head -n 1', desc: '/mnt/k/ 경로 접근' },
        { cmd: 'echo $TMPDIR', desc: 'TMPDIR 환경변수' }
    ];

    const pathIssues = [];

    for (const test of testCommands) {
        try {
            const result = execSync(`"${bashPath}" -c "${test.cmd}"`, {
                encoding: 'utf8',
                windowsHide: true,
                cwd: 'K:\\PortableApps\\Claude-Code'
            }).trim();

            if (result) {
                console.log(`${colors.green}✓${colors.reset} ${test.desc}: ${result.substring(0, 50)}`);
                report.checks.push({
                    name: test.desc,
                    status: 'PASS',
                    result: result.substring(0, 100)
                });
            } else {
                console.log(`${colors.yellow}⚠${colors.reset} ${test.desc}: 응답 없음`);
                pathIssues.push(test.desc);
            }
        } catch (error) {
            console.log(`${colors.red}✗${colors.reset} ${test.desc}: 실패`);
            pathIssues.push(test.desc);
            report.checks.push({
                name: test.desc,
                status: 'FAIL',
                error: error.message.substring(0, 100)
            });
        }
    }

    return pathIssues;
}

// 3. 자동 수정 실행
function autoFix(bashPath, issues) {
    console.log('\n3️⃣ 자동 수정 실행...');

    if (issues.length === 0) {
        console.log(`${colors.green}✓${colors.reset} 수정할 문제가 없습니다.`);
        return;
    }

    // 수정 스크립트 생성
    const fixes = [];

    // /k/ 경로 문제 수정
    if (issues.some(i => i.includes('/k/ 경로'))) {
        console.log('📝 /k/ → /cygdrive/k/ 심볼릭 링크 생성 시도...');

        const linkScript = `
@echo off
echo Creating /k symlink to /cygdrive/k...
"${bashPath}" -c "ln -sf /cygdrive/k /k 2>/dev/null || echo Link exists"
echo Done.
`;

        const scriptPath = 'K:\\PortableApps\\Claude-Code\\temp-fix-link.bat';
        fs.writeFileSync(scriptPath, linkScript);

        try {
            execSync(scriptPath, { windowsHide: true });
            console.log(`${colors.green}✓${colors.reset} 심볼릭 링크 생성 완료`);
            fixes.push('Created /k symlink');
            report.fixes.push({
                type: 'Symlink',
                status: 'SUCCESS'
            });
        } catch (error) {
            console.log(`${colors.yellow}⚠${colors.reset} 심볼릭 링크 생성 실패 (권한 문제일 수 있음)`);
            report.fixes.push({
                type: 'Symlink',
                status: 'FAILED',
                error: error.message
            });
        }

        // 정리
        try { fs.unlinkSync(scriptPath); } catch {}
    }

    // TMPDIR 문제 수정
    if (issues.some(i => i.includes('TMPDIR'))) {
        console.log('📝 TMPDIR 설정...');

        const tmpDir = 'K:\\PortableApps\\Claude-Code\\tmp';
        if (!fs.existsSync(tmpDir)) {
            fs.mkdirSync(tmpDir, { recursive: true });
            console.log(`${colors.green}✓${colors.reset} tmp 디렉토리 생성: ${tmpDir}`);
            fixes.push('Created tmp directory');
        }

        // 환경 변수 설정 스크립트
        const envScript = `
@echo off
setx TMPDIR "K:\\PortableApps\\Claude-Code\\tmp"
setx TMP "K:\\PortableApps\\Claude-Code\\tmp"
setx TEMP "K:\\PortableApps\\Claude-Code\\tmp"
echo Environment variables set.
`;

        const envPath = 'K:\\PortableApps\\Claude-Code\\temp-fix-env.bat';
        fs.writeFileSync(envPath, envScript);

        try {
            execSync(envPath, { windowsHide: true });
            console.log(`${colors.green}✓${colors.reset} 환경 변수 설정 완료`);
            fixes.push('Set TMPDIR environment');
            report.fixes.push({
                type: 'Environment',
                status: 'SUCCESS'
            });
        } catch (error) {
            console.log(`${colors.yellow}⚠${colors.reset} 환경 변수 설정 실패`);
            report.fixes.push({
                type: 'Environment',
                status: 'FAILED',
                error: error.message
            });
        }

        // 정리
        try { fs.unlinkSync(envPath); } catch {}
    }

    return fixes;
}

// 4. Claude Code 특정 문제 분석
function analyzeClaudeCode() {
    console.log('\n4️⃣ Claude Code 특정 문제 분석...');

    // shell-snapshots 디렉토리 확인
    const snapshotDir = 'K:\\PortableApps\\Claude-Code\\shell-snapshots';
    if (fs.existsSync(snapshotDir)) {
        const files = fs.readdirSync(snapshotDir);
        console.log(`📁 Shell snapshots: ${files.length}개 파일`);

        if (files.length > 10) {
            console.log(`${colors.yellow}⚠${colors.reset} 너무 많은 snapshot 파일! 정리 필요`);

            // 오래된 파일 삭제
            const now = Date.now();
            let deleted = 0;
            for (const file of files) {
                const filePath = path.join(snapshotDir, file);
                const stats = fs.statSync(filePath);
                const age = now - stats.mtimeMs;

                if (age > 3600000) { // 1시간 이상 된 파일
                    fs.unlinkSync(filePath);
                    deleted++;
                }
            }

            if (deleted > 0) {
                console.log(`${colors.green}✓${colors.reset} ${deleted}개의 오래된 snapshot 삭제`);
                report.fixes.push({
                    type: 'Cleanup',
                    status: 'SUCCESS',
                    detail: `Deleted ${deleted} old snapshots`
                });
            }
        }
    }

    // .claude.json 확인
    const claudeConfig = 'K:\\PortableApps\\Claude-Code\\.claude.json';
    if (fs.existsSync(claudeConfig)) {
        try {
            const config = JSON.parse(fs.readFileSync(claudeConfig, 'utf8'));
            console.log(`${colors.green}✓${colors.reset} .claude.json 파일 정상`);

            // bash 경로 확인
            if (config.shellIntegration?.gitBash?.path) {
                const configuredBash = config.shellIntegration.gitBash.path;
                if (!fs.existsSync(configuredBash)) {
                    console.log(`${colors.red}✗${colors.reset} 설정된 bash 경로가 잘못됨: ${configuredBash}`);
                    report.errors.push(`Invalid bash path in config: ${configuredBash}`);
                }
            }
        } catch (error) {
            console.log(`${colors.red}✗${colors.reset} .claude.json 파싱 실패`);
            report.errors.push('Failed to parse .claude.json');
        }
    }
}

// 5. 최종 리포트 생성
function generateReport(bashPath, issues, fixes) {
    console.log('\n5️⃣ 최종 리포트 생성...');

    // 상태 결정
    if (issues.length === 0) {
        report.status = 'HEALTHY';
        console.log(`\n${colors.green}✅ 시스템 상태: 정상${colors.reset}`);
    } else if (fixes.length > 0) {
        report.status = 'FIXED';
        console.log(`\n${colors.yellow}⚠️ 시스템 상태: 부분 수정됨${colors.reset}`);
    } else {
        report.status = 'NEEDS_MANUAL_FIX';
        console.log(`\n${colors.red}❌ 시스템 상태: 수동 수정 필요${colors.reset}`);
    }

    // 리포트 파일 저장
    const reportPath = 'K:\\PortableApps\\Claude-Code\\auto-check-report.json';
    fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));
    console.log(`\n📄 상세 리포트: ${reportPath}`);

    // 요약 출력
    console.log('\n' + '='.repeat(50));
    console.log('📊 검증 요약:');
    console.log(`  - 검사 항목: ${report.checks.length}개`);
    console.log(`  - 성공: ${report.checks.filter(c => c.status === 'PASS').length}개`);
    console.log(`  - 실패: ${report.checks.filter(c => c.status === 'FAIL').length}개`);
    console.log(`  - 수정 시도: ${report.fixes.length}개`);
    console.log(`  - 에러: ${report.errors.length}개`);
    console.log('='.repeat(50));

    // 권장 사항
    if (report.status === 'NEEDS_MANUAL_FIX') {
        console.log('\n💡 권장 사항:');
        console.log('1. Git Bash를 최신 버전으로 업데이트');
        console.log('2. 관리자 권한으로 Claude Code 실행');
        console.log('3. K드라이브가 제대로 마운트되었는지 확인');
        console.log('4. 필요시 Claude Code 재설치');
    }
}

// 메인 실행
async function main() {
    try {
        // 1. Bash 찾기
        const bashPath = checkBashExecutable();
        if (!bashPath) {
            report.status = 'CRITICAL_ERROR';
            throw new Error('Bash를 찾을 수 없습니다. Git을 설치해주세요.');
        }

        // 2. 경로 문제 진단
        const issues = diagnosePath(bashPath);

        // 3. 자동 수정
        const fixes = autoFix(bashPath, issues);

        // 4. Claude Code 분석
        analyzeClaudeCode();

        // 5. 리포트 생성
        generateReport(bashPath, issues, fixes || []);

        console.log('\n✨ AUTO-CHECK 완료!');

    } catch (error) {
        console.error(`\n${colors.red}❌ 치명적 오류: ${error.message}${colors.reset}`);
        report.status = 'CRITICAL_ERROR';
        report.errors.push(error.message);

        // 에러 리포트 저장
        const reportPath = 'K:\\PortableApps\\Claude-Code\\auto-check-error.json';
        fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));
        console.log(`\n📄 에러 리포트: ${reportPath}`);

        process.exit(1);
    }
}

// 실행
main();

#!/usr/bin/env node
/**
 * SAFE-MONITOR.js - 순환참조 없는 안전한 Bash 환경 모니터링
 * 
 * 핵심 안전 원칙:
 * 1. Bash를 통해 Bash를 수정하지 않음
 * 2. 읽기 전용 검증만 수행
 * 3. 수정은 별도 프로세스로 분리
 * 4. 모든 작업에 롤백 가능
 */

const fs = require('fs');
const path = require('path');
const { spawn } = require('child_process');

console.log('🛡️ SAFE-MONITOR: 안전한 환경 검증 시작...\n');

// 순환참조 체크
const circularRefs = [];
const safetyReport = {
    timestamp: new Date().toISOString(),
    circularReferences: [],
    dangerousPatterns: [],
    safeChecks: [],
    recommendations: []
};

// 1. 파일 시스템 기반 검증 (Bash 실행 없이)
function safeFileSystemCheck() {
    console.log('📁 파일 시스템 검증 (Bash 독립적)...');

    const checks = [
        {
            name: 'K드라이브 접근',
            path: 'K:\\PortableApps\\Claude-Code',
            type: 'directory'
        },
        {
            name: 'Git Bash 실행 파일',
            path: 'K:\\PortableApps\\tools\\git\\bin\\bash.exe',
            type: 'file'
        },
        {
            name: 'Node.js 실행 파일',
            path: 'K:\\PortableApps\\tools\\nodejs\\node.exe',
            type: 'file'
        },
        {
            name: 'Python 실행 파일',  
            path: 'K:\\PortableApps\\tools\\python\\python.exe',
            type: 'file'
        },
        {
            name: 'tmp 디렉토리',
            path: 'K:\\PortableApps\\Claude-Code\\tmp',
            type: 'directory'
        }
    ];

    for (const check of checks) {
        try {
            if (fs.existsSync(check.path)) {
                const stats = fs.statSync(check.path);
                const isCorrectType = check.type === 'directory' ? stats.isDirectory() : stats.isFile();

                if (isCorrectType) {
                    console.log(`✅ ${check.name}: 정상`);
                    safetyReport.safeChecks.push({
                        name: check.name,
                        status: 'PASS',
                        path: check.path
                    });
                } else {
                    console.log(`⚠️ ${check.name}: 타입 불일치`);
                    safetyReport.dangerousPatterns.push({
                        type: 'TYPE_MISMATCH',
                        path: check.path
                    });
                }
            } else {
                console.log(`❌ ${check.name}: 없음`);
                safetyReport.recommendations.push(`Create ${check.name} at ${check.path}`);
            }
        } catch (error) {
            console.log(`⚠️ ${check.name}: 접근 오류`);
        }
    }
}

// 2. 순환참조 패턴 감지
function detectCircularReferences() {
    console.log('\n🔄 순환참조 패턴 감지...');

    const scriptsToCheck = [
        'K:\\PortableApps\\Claude-Code\\AUTO-CHECK.js',
        'K:\\PortableApps\\Claude-Code\\FIX-BASH-NOW.bat',
        'K:\\PortableApps\\Claude-Code\\ULTIMATE-BASH-FIX.bat'
    ];

    for (const scriptPath of scriptsToCheck) {
        if (fs.existsSync(scriptPath)) {
            const content = fs.readFileSync(scriptPath, 'utf8');

            // 위험한 패턴 검사
            const dangerousPatterns = [
                {
                    pattern: /bash.*-c.*bash/i,
                    description: 'Bash를 통해 Bash 실행'
                },
                {
                    pattern: /execSync.*bash.*fix.*bash/i,
                    description: 'Bash로 Bash 수정 시도'
                },
                {
                    pattern: /fs\.watch.*\.claude\.json.*fs\.write/i,
                    description: '파일 감시 중 같은 파일 수정'
                },
                {
                    pattern: /while.*true.*execSync/i,
                    description: '무한 루프 내 명령 실행'
                }
            ];

            for (const dp of dangerousPatterns) {
                if (dp.pattern.test(content)) {
                    console.log(`⚠️ 순환참조 위험: ${path.basename(scriptPath)} - ${dp.description}`);
                    safetyReport.circularReferences.push({
                        file: scriptPath,
                        pattern: dp.description
                    });
                }
            }
        }
    }

    if (safetyReport.circularReferences.length === 0) {
        console.log('✅ 순환참조 패턴 없음');
    } else {
        console.log(`❌ ${safetyReport.circularReferences.length}개 순환참조 위험 발견!`);
    }
}

// 3. Shell snapshots 안전 검사
function checkShellSnapshots() {
    console.log('\n📸 Shell Snapshots 검사...');

    const snapshotDir = 'K:\\PortableApps\\Claude-Code\\shell-snapshots';

    if (fs.existsSync(snapshotDir)) {
        const files = fs.readdirSync(snapshotDir);
        console.log(`📊 Snapshot 파일 수: ${files.length}개`);

        // 파일 나이 분석
        const now = Date.now();
        let oldCount = 0;
        let lockedCount = 0;

        for (const file of files) {
            const filePath = path.join(snapshotDir, file);
            try {
                const stats = fs.statSync(filePath);
                const ageHours = (now - stats.mtimeMs) / 3600000;

                if (ageHours > 1) {
                    oldCount++;
                }

                // 파일 잠금 테스트 (읽기 시도)
                try {
                    const fd = fs.openSync(filePath, 'r');
                    fs.closeSync(fd);
                } catch {
                    lockedCount++;
                }
            } catch (error) {
                console.log(`⚠️ 파일 접근 오류: ${file}`);
            }
        }

        if (oldCount > 0) {
            console.log(`🗑️ 정리 가능: ${oldCount}개 오래된 snapshot`);
            safetyReport.recommendations.push(`Clean ${oldCount} old snapshots`);
        }

        if (lockedCount > 0) {
            console.log(`🔒 잠긴 파일: ${lockedCount}개 (Claude Code 실행 중)`);
            safetyReport.dangerousPatterns.push({
                type: 'LOCKED_FILES',
                count: lockedCount
            });
        }
    }
}

// 4. 환경 변수 검사 (안전하게)
function checkEnvironmentSafely() {
    console.log('\n🌍 환경 변수 검사...');

    const importantVars = [
        'CLAUDE_HOME',
        'CLAUDE_CONFIG_FILE',
        'TMPDIR',
        'TEMP',
        'TMP',
        'PATH'
    ];

    for (const varName of importantVars) {
        const value = process.env[varName];
        if (value) {
            // 경로 유효성 검사
            if (varName.includes('DIR') || varName.includes('HOME') || varName.includes('FILE')) {
                if (value.startsWith('K:') || value.startsWith('C:')) {
                    console.log(`✅ ${varName}: ${value.substring(0, 50)}...`);
                } else if (value.startsWith('/')) {
                    console.log(`⚠️ ${varName}: Unix 스타일 경로 - ${value.substring(0, 50)}...`);
                    safetyReport.dangerousPatterns.push({
                        type: 'UNIX_PATH_IN_WINDOWS',
                        variable: varName,
                        value: value.substring(0, 100)
                    });
                }
            }
        } else {
            console.log(`❌ ${varName}: 설정되지 않음`);
        }
    }
}

// 5. 안전한 수정 제안 생성
function generateSafeFixes() {
    console.log('\n💡 안전한 수정 제안...');

    const fixes = [];

    // 순환참조가 있으면 수정 제안
    if (safetyReport.circularReferences.length > 0) {
        fixes.push({
            issue: 'Circular References',
            solution: 'Refactor scripts to avoid bash-through-bash patterns',
            script: 'SAFE-FIX-CIRCULAR.bat'
        });
    }

    // Unix 경로 문제가 있으면
    const unixPaths = safetyReport.dangerousPatterns.filter(d => d.type === 'UNIX_PATH_IN_WINDOWS');
    if (unixPaths.length > 0) {
        fixes.push({
            issue: 'Unix Path in Windows',
            solution: 'Create Windows-compatible path mappings',
            script: 'SAFE-FIX-PATHS.bat'
        });
    }

    // 안전한 수정 스크립트 생성
    if (fixes.length > 0) {
        const safeFix = `@echo off
REM SAFE-FIX.bat - 안전한 수정 스크립트
REM 생성 시각: ${new Date().toISOString()}
REM 순환참조 없이 안전하게 수정

echo 🛡️ 안전한 수정 시작...

REM 1. 백업 생성
echo 📦 백업 생성 중...
if not exist "BACKUP" mkdir BACKUP
copy .claude.json BACKUP\\.claude.json.%date:~-4%%date:~3,2%%date:~0,2%.backup >nul 2>&1

REM 2. tmp 디렉토리 생성
if not exist "tmp" (
    echo 📁 tmp 디렉토리 생성...
    mkdir tmp
)

REM 3. 환경 변수 설정 (시스템 수준)
echo 🌍 환경 변수 설정...
setx TMPDIR "%cd%\\tmp" >nul 2>&1
setx CLAUDE_HOME "%cd%" >nul 2>&1

REM 4. 심볼릭 링크 (PowerShell 사용 - Bash 독립적)
echo 🔗 경로 매핑 생성...
powershell -Command "if (Test-Path 'K:') { Write-Host 'K: 드라이브 확인됨' } else { Write-Host 'K: 드라이브 없음' }"

echo ✅ 안전한 수정 완료!
echo.
echo 📝 다음 단계:
echo 1. Claude Code를 재시작하세요
echo 2. SAFE-MONITOR.js를 다시 실행하여 확인하세요
pause
`;

        fs.writeFileSync('K:\\PortableApps\\Claude-Code\\SAFE-FIX.bat', safeFix);
        console.log('✅ SAFE-FIX.bat 생성 완료');
    }

    return fixes;
}

// 6. 최종 보고서
function generateSafetyReport() {
    console.log('\n📊 안전성 검증 보고서\n');
    console.log('='.repeat(50));

    // 위험도 계산
    const riskLevel = 
        safetyReport.circularReferences.length * 10 +
        safetyReport.dangerousPatterns.length * 5;

    let status = '🟢 안전';
    if (riskLevel > 20) status = '🔴 위험';
    else if (riskLevel > 10) status = '🟡 주의';

    console.log(`상태: ${status} (위험도: ${riskLevel})`);
    console.log(`순환참조: ${safetyReport.circularReferences.length}개`);
    console.log(`위험 패턴: ${safetyReport.dangerousPatterns.length}개`);
    console.log(`안전 확인: ${safetyReport.safeChecks.filter(c => c.status === 'PASS').length}개`);
    console.log(`권장사항: ${safetyReport.recommendations.length}개`);
    console.log('='.repeat(50));

    // JSON 보고서 저장
    const reportPath = 'K:\\PortableApps\\Claude-Code\\safety-report.json';
    fs.writeFileSync(reportPath, JSON.stringify(safetyReport, null, 2));
    console.log(`\n📄 상세 보고서: ${reportPath}`);

    // 순환참조 상세
    if (safetyReport.circularReferences.length > 0) {
        console.log('\n⚠️ 발견된 순환참조:');
        safetyReport.circularReferences.forEach((ref, i) => {
            console.log(`  ${i+1}. ${path.basename(ref.file)}: ${ref.pattern}`);
        });
    }

    // 권장사항
    if (safetyReport.recommendations.length > 0) {
        console.log('\n💡 권장사항:');
        safetyReport.recommendations.forEach((rec, i) => {
            console.log(`  ${i+1}. ${rec}`);
        });
    }
}

// 메인 실행
async function main() {
    try {
        // 1. 파일 시스템 검사
        safeFileSystemCheck();

        // 2. 순환참조 감지
        detectCircularReferences();

        // 3. Shell snapshots 검사
        checkShellSnapshots();

        // 4. 환경 변수 검사
        checkEnvironmentSafely();

        // 5. 안전한 수정 제안
        const fixes = generateSafeFixes();

        // 6. 보고서 생성
        generateSafetyReport();

        console.log('\n✨ SAFE-MONITOR 완료!');
        console.log('🛡️ 순환참조 없이 안전하게 검증했습니다.\n');

    } catch (error) {
        console.error(`\n❌ 오류: ${error.message}`);
        safetyReport.error = error.message;

        // 에러 보고서 저장
        const errorPath = 'K:\\PortableApps\\Claude-Code\\safety-error.json';
        fs.writeFileSync(errorPath, JSON.stringify(safetyReport, null, 2));
        console.log(`📄 에러 보고서: ${errorPath}`);
    }
}

// 실행
main();

