const fs = require('fs');
const path = require('path');

console.log('=== MCP 서버 상태 직접 점검 ===\n');

// .claude.json 읽기
const configPath = 'K:/PortableApps/Claude-Code/.claude.json';
const config = JSON.parse(fs.readFileSync(configPath, 'utf8'));

const mcpServers = config.mcpServers || {};

console.log(`총 ${Object.keys(mcpServers).length}개 MCP 서버 설정됨\n`);

// 각 MCP 서버 점검
for (const [name, server] of Object.entries(mcpServers)) {
    console.log(`[${name}]`);
    console.log(`  Type: ${server.type}`);
    console.log(`  Command: ${server.command}`);

    // 실행 파일 경로 확인
    if (server.command.includes('K:')) {
        const exists = fs.existsSync(server.command);
        console.log(`  실행 파일: ${exists ? '✅ 존재' : '❌ 없음'} - ${server.command}`);
    }

    // args에 있는 파일 경로 확인
    if (server.args) {
        for (const arg of server.args) {
            if (arg.includes('K:\\') || arg.includes('K:/')) {
                const normalizedPath = arg.replace(/\\/g, '/');
                const exists = fs.existsSync(normalizedPath);
                if (normalizedPath.includes('.js') || normalizedPath.includes('.py')) {
                    console.log(`  스크립트: ${exists ? '✅ 존재' : '❌ 없음'} - ${normalizedPath}`);
                }
            }
        }
    }

    // 환경 변수 확인
    if (server.env) {
        const envKeys = Object.keys(server.env);
        if (envKeys.length > 0) {
            console.log(`  환경변수: ${envKeys.join(', ')}`);
        }
    }

    console.log();
}

// 중요 파일들 확인
console.log('=== 중요 파일 존재 여부 ===');
const importantFiles = [
    'K:/PortableApps/tools/nodejs/node.exe',
    'K:/PortableApps/tools/python/python.exe',
    'K:/PortableApps/tools/git/bin/bash.exe',
    'K:/PortableApps/Claude-Code/node_modules/@anthropic-ai/claude-code/cli.js',
    'K:/PortableApps/Claude-Code/ShrimpData/current-tasks.json'
];

for (const file of importantFiles) {
    const exists = fs.existsSync(file);
    console.log(`${exists ? '✅' : '❌'} ${file}`);
}

const fs = require('fs');
const path = require('path');

console.log('=== 최근 24시간 내 변경된 파일 검사 ===\n');

const now = Date.now();
const oneDayAgo = now - (24 * 60 * 60 * 1000);

function scanDirectory(dir, indent = '') {
    const items = fs.readdirSync(dir);
    const results = [];

    for (const item of items) {
        const fullPath = path.join(dir, item);

        // 제외 디렉토리
        if (item === 'node_modules' || item === '.git' || item === 'ARCHIVE') continue;

        try {
            const stats = fs.statSync(fullPath);

            if (stats.mtime.getTime() > oneDayAgo) {
                results.push({
                    path: fullPath,
                    modified: stats.mtime,
                    size: stats.size,
                    isDir: stats.isDirectory()
                });
            }

            if (stats.isDirectory() && item !== 'shell-snapshots') {
                results.push(...scanDirectory(fullPath, indent + '  '));
            }
        } catch (err) {
            // 접근 불가 파일 무시
        }
    }

    return results;
}

const recentFiles = scanDirectory('K:/PortableApps/Claude-Code');

// 시간순 정렬
recentFiles.sort((a, b) => b.modified - a.modified);

console.log(`총 ${recentFiles.length}개 파일이 최근 24시간 내 변경됨\n`);

// 상위 20개만 표시
const displayCount = Math.min(20, recentFiles.length);
for (let i = 0; i < displayCount; i++) {
    const file = recentFiles[i];
    const relativePath = file.path.replace('K:\\PortableApps\\Claude-Code\\', '');
    const timeAgo = Math.floor((now - file.modified) / 1000 / 60);
    console.log(`${file.isDir ? '[DIR]' : '[FILE]'} ${relativePath}`);
    console.log(`  수정: ${timeAgo}분 전 | 크기: ${file.size} bytes`);
}

// 잠재적 문제 파일 찾기
console.log('\n=== 잠재적 문제 파일 ===');
const problemPatterns = [
    { pattern: /\.backup/i, desc: '백업 파일' },
    { pattern: /\.broken/i, desc: '손상된 파일' },
    { pattern: /\.old/i, desc: '이전 버전' },
    { pattern: /\.tmp/i, desc: '임시 파일' }
