const cleanupTargets = [];
const patterns = [
    { pattern: /\.backup\d*$/, desc: '백업 파일' },
    { pattern: /\.old$/, desc: '이전 버전' },
    { pattern: /\.tmp$/, desc: '임시 파일' },
    { pattern: /\.broken/, desc: '손상된 파일' },
    { pattern: /^~/, desc: '임시 편집 파일' }
];

function findCleanupFiles(dir) {
    try {
        const items = fs.readdirSync(dir);
        for (const item of items) {
            const fullPath = path.join(dir, item);

            // 제외 디렉토리
            if (item === 'node_modules' || item === '.git' || item === 'tools') continue;

            for (const { pattern, desc } of patterns) {
                if (pattern.test(item)) {
                    const stats = fs.statSync(fullPath);
                    cleanupTargets.push({
                        path: fullPath,
                        size: stats.size,
                        type: desc,
                        age: Date.now() - stats.mtime
                    });
                }
            }

            // 하위 디렉토리 검색
            if (fs.statSync(fullPath).isDirectory() && item !== 'ARCHIVE') {
                findCleanupFiles(fullPath);
            }
        }
    } catch (err) {
        // 무시
    }
}

findCleanupFiles('K:/PortableApps/Claude-Code');

