console.log();

let monitorCount = 0;
const issues = [];

function checkStatus() {
    monitorCount++;
    const now = new Date().toLocaleTimeString('ko-KR');

    console.log(`\n[${now}] 모니터링 #${monitorCount}`);
    console.log('─'.repeat(60));

    // 1. 프로세스 확인
    exec('tasklist | findstr /i "node.exe"', (err, stdout) => {
        const nodeProcesses = stdout.split('\n').filter(line => line.includes('node.exe')).length;
        console.log(`  Node.js 프로세스: ${nodeProcesses}개 실행 중`);
    });

    // 2. 메모리 사용량
    const memUsage = process.memoryUsage();
    const memMB = (memUsage.heapUsed / 1024 / 1024).toFixed(2);
    console.log(`  메모리 사용: ${memMB} MB`);

    // 3. Shell snapshots 확인
    const snapshotDir = 'K:/PortableApps/Claude-Code/shell-snapshots';
    if (fs.existsSync(snapshotDir)) {
        const files = fs.readdirSync(snapshotDir);
        const snapshots = files.filter(f => f.endsWith('.sh'));
        if (snapshots.length > 0) {
            console.log(`  ⚠️ Shell snapshot 에러 감지: ${snapshots.length}개`);
            issues.push(`Shell snapshot 문제 (${now})`);

            // 자동 정리
            snapshots.forEach(f => {
                const archivePath = path.join(snapshotDir, 'archive', f);
                fs.renameSync(path.join(snapshotDir, f), archivePath);
            });
            console.log(`  ✅ 자동 정리 완료`);
        } else {
            console.log(`  ✅ Shell snapshots 정상`);
        }
    }

    // 4. 환경변수 체크
    const criticalEnvs = [
        'CLAUDE_CODE_GIT_BASH_PATH',
        'ANTHROPIC_API_KEY'
    ];

    let envOK = true;
    criticalEnvs.forEach(env => {
        if (!process.env[env]) {
            console.log(`  ❌ ${env} 미설정`);
            envOK = false;
        }
    });

    if (envOK) {
        console.log(`  ✅ 모든 필수 환경변수 정상`);
    }

    // 5. .claude.json 무결성
    try {
        const claudeJson = JSON.parse(fs.readFileSync('K:/PortableApps/Claude-Code/.claude.json', 'utf8'));
        const mcpCount = Object.keys(claudeJson.mcpServers || {}).length;
        console.log(`  MCP 서버: ${mcpCount}개 설정됨`);

        // 타임스탬프 체크
        if (claudeJson.claudeCodeFirstTokenDate) {
            const date = new Date(claudeJson.claudeCodeFirstTokenDate);
            const month = date.getMonth() + 1;
            if (month !== 8) {
                console.log(`  ⚠️ 타임스탬프 오류 감지 (${month}월)`);

                // 자동 수정
                claudeJson.claudeCodeFirstTokenDate = claudeJson.claudeCodeFirstTokenDate.replace('-01-', '-08-');
                fs.writeFileSync('K:/PortableApps/Claude-Code/.claude.json', JSON.stringify(claudeJson, null, 2));
                console.log(`  ✅ 타임스탬프 자동 수정됨`);
            }
        }
    } catch (e) {
        console.log(`  ❌ .claude.json 읽기 실패`);
        issues.push(`.claude.json 오류 (${now})`);
    }

    // 6. 디스크 공간
    exec('wmic logicaldisk where "DeviceID=\'K:\'" get FreeSpace /format:value', (err, stdout) => {
        if (!err && stdout) {
            const match = stdout.match(/FreeSpace=(\d+)/);
            if (match) {
                const freeGB = (parseInt(match[1]) / (1024**3)).toFixed(1);
                console.log(`  디스크 여유: ${freeGB} GB`);

                if (parseFloat(freeGB) < 5) {
                    console.log(`  ⚠️ 디스크 공간 부족 경고!`);
                    issues.push(`디스크 공간 부족 (${now})`);
                }
            }
        }
    });

    // 이슈 요약
    if (issues.length > 0) {
        console.log('\n📋 감지된 문제들:');
        issues.slice(-5).forEach(issue => console.log(`  • ${issue}`));
    }

    // 상태 표시
    console.log('\n상태: ' + (issues.length === 0 ? '✅ 모든 시스템 정상' : '⚠️ 문제 감지 및 자동 수정 중'));
}

