console.log('종료하려면 Ctrl+C를 누르세요.\n');

// 종료 핸들러
process.on('SIGINT', () => {
    console.log('\n\n모니터링 종료됨');
    console.log(`총 ${monitorCount}회 모니터링 수행`);
    console.log(`발견된 문제: ${issues.length}개`);
    process.exit(0);
});

