const GREEN = '\x1b[32m';
const YELLOW = '\x1b[33m';
const RESET = '\x1b[0m';

let totalIssues = 0;
let criticalIssues = 0;

function checkItem(name, condition, critical = false) {
    if (condition) {
        console.log(`${GREEN}✅${RESET} ${name}`);
        return true;
    } else {
        console.log(`${critical ? RED : YELLOW}${critical ? '❌' : '⚠️'}${RESET} ${name}`);
        totalIssues++;
        if (critical) criticalIssues++;
        return false;
    }
}

function runCommand(cmd) {
    try {
        return execSync(cmd, { encoding: 'utf8' }).trim();
    } catch (e) {
        return null;
    }
}

// 1. 핵심 파일 존재 확인
console.log('[1] 핵심 파일 존재 확인');
console.log('-'.repeat(40));

const essentialFiles = [
    { path: 'K:/PortableApps/tools/git/bin/bash.exe', name: 'Git Bash', critical: true },
    { path: 'K:/PortableApps/tools/nodejs/node.exe', name: 'Node.js', critical: true },
    { path: 'K:/PortableApps/tools/python/python.exe', name: 'Python', critical: false },
    { path: 'K:/PortableApps/Claude-Code/claude.bat', name: 'Claude.bat', critical: true },
