console.log('\n[2] 환경변수 설정');
console.log('-'.repeat(40));

const envVars = [
    { name: 'CLAUDE_CODE_GIT_BASH_PATH', critical: true },
    { name: 'NODE_PATH', critical: false },
    { name: 'PYTHONPATH', critical: false },
    { name: 'CLAUDE_HOME', critical: false },
    { name: 'ANTHROPIC_API_KEY', critical: true }
];

envVars.forEach(env => {
    const value = process.env[env.name];
    if (value) {
        // 길이가 길면 축약
        const display = value.length > 50 ? value.substring(0, 47) + '...' : value;
        checkItem(`${env.name} = ${display}`, true);
    } else {
        checkItem(`${env.name} (미설정)`, false, env.critical);
    }
});

