console.log('-'.repeat(40));

const pathValue = process.env.PATH || '';
checkItem('nodejs in PATH', pathValue.includes('PortableApps\\tools\\nodejs'));
checkItem('git in PATH', pathValue.includes('PortableApps\\tools\\git'));
checkItem('python in PATH', pathValue.includes('PortableApps\\tools\\python'));

// 4. 버전 확인
console.log('\n[4] 도구 버전 확인');
console.log('-'.repeat(40));

const nodeVersion = runCommand('K:\\PortableApps\\tools\\nodejs\\node.exe --version');
const gitVersion = runCommand('K:\\PortableApps\\tools\\git\\bin\\git.exe --version');
