foreach ($var in $envVars) {
    $value = [System.Environment]::GetEnvironmentVariable($var)
    if ($value) {
        if ($value.Length -gt 50) {
            $display = $value.Substring(0, 47) + "..."
        } else {
            $display = $value
        }
        Write-Host "✅ ${var}: $display" -ForegroundColor Green
    } else {
        Write-Host "⚠️ ${var}: 설정되지 않음" -ForegroundColor Yellow
    }
}

# 6. 진단 스크립트 실행
Write-Host ""
Write-Host "[6] 진단 스크립트 실행" -ForegroundColor Green
if ((Test-Path $nodeExe) -and (Test-Path "K:\PortableApps\Claude-Code\direct-claude-test.js")) {
    Write-Host "Node.js로 진단 스크립트 실행 중..." -ForegroundColor Yellow
    & $nodeExe "K:\PortableApps\Claude-Code\direct-claude-test.js"
} else {
    Write-Host "❌ 진단 스크립트를 실행할 수 없음" -ForegroundColor Red
}

Write-Host ""
Write-Host "============================================" -ForegroundColor Cyan
Write-Host "진단 완료!" -ForegroundColor Green

