@echo off
REM PowerShell 대체 실행 환경 래퍼
REM 사용법: pwsh.bat [명령]

if "%1"=="" (
    powershell -ExecutionPolicy Bypass -File "K:\PortableApps\Claude-Code\RUN-POWERSHELL.ps1"
) else (
    powershell -ExecutionPolicy Bypass -File "K:\PortableApps\Claude-Code\RUN-POWERSHELL.ps1" %1
)

// Claude 직접 실행 테스트 스크립트
const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

console.log('=' .repeat(60));
console.log('    CLAUDE CODE 직접 실행 테스트');
console.log('=' .repeat(60));
console.log();

const nodeExe = 'K:\\PortableApps\\tools\\nodejs\\node.exe';
const claudeCli = 'K:\\PortableApps\\Claude-Code\\node_modules\\@anthropic-ai\\claude-code\\cli.js';

// 테스트 항목들
const tests = [
    {
        name: 'Claude CLI 파일 존재',
        check: () => fs.existsSync(claudeCli)
    },
    {
        name: 'Node.js 실행 파일 존재',
        check: () => fs.existsSync(nodeExe)
    },
    {
        name: 'Claude --version 실행',
        check: () => {
            try {
                const cmd = `"${nodeExe}" "${claudeCli}" --version`;
                const result = execSync(cmd, { encoding: 'utf8' }).trim();
                console.log(`  버전: ${result}`);
                return result.includes('claude-code');
            } catch (e) {
                console.log(`  에러: ${e.message}`);
                return false;
            }
        }
    },
    {
        name: 'Claude mcp list 실행',
        check: () => {
            try {
                const cmd = `"${nodeExe}" "${claudeCli}" mcp list`;
                const result = execSync(cmd, { encoding: 'utf8' });
                const lines = result.split('\n');
                const mcpCount = lines.filter(l => l.includes('✓')).length;
                console.log(`  MCP 서버 수: ${mcpCount}개`);
                return mcpCount > 0;
            } catch (e) {
                console.log(`  에러: ${e.message}`);
                return false;
            }
        }
    }
];

// 테스트 실행
let passed = 0;
let failed = 0;

tests.forEach(test => {
    console.log(`[테스트] ${test.name}`);
    const result = test.check();
    if (result) {
        console.log(`  ✅ 성공`);
        passed++;
    } else {
        console.log(`  ❌ 실패`);
        failed++;
    }
    console.log();
});

// 결과 요약
console.log('=' .repeat(60));
console.log('📊 테스트 결과');
console.log('=' .repeat(60));
console.log(`성공: ${passed}개`);
console.log(`실패: ${failed}개`);

if (failed === 0) {
    console.log();
    console.log('🎉 모든 테스트 통과! Claude가 정상 작동합니다.');
} else {
    console.log();
    console.log('⚠️ 일부 테스트 실패. 추가 조치가 필요합니다.');
}

console.log('=' .repeat(60));

@echo off
echo ============================================
echo    CLAUDE CODE 실행 검증
echo ============================================
echo.

echo [1] Node.js 버전 확인...
"K:\PortableApps\tools\nodejs\node.exe" --version
echo.

echo [2] Claude CLI 파일 확인...
if exist "K:\PortableApps\Claude-Code\node_modules\@anthropic-ai\claude-code\cli.js" (
    echo    ✓ Claude CLI 파일 존재
) else (
    echo    X Claude CLI 파일 없음
    goto :error
)
echo.

echo [3] Claude 버전 확인...
"K:\PortableApps\tools\nodejs\node.exe" "K:\PortableApps\Claude-Code\node_modules\@anthropic-ai\claude-code\cli.js" --version
echo.

echo [4] Claude MCP 목록 확인...
"K:\PortableApps\tools\nodejs\node.exe" "K:\PortableApps\Claude-Code\node_modules\@anthropic-ai\claude-code\cli.js" mcp list
echo.

echo ============================================
echo    ✓ Claude가 정상 작동합니다!
echo ============================================
goto :end

:error
echo ============================================
echo    X Claude 실행 실패
echo ============================================

:end
pause

