} catch (err) {
    console.log('❌ .claude.json 파일 읽기 실패:', err.message);
    process.exit(1);
}

// 백업 생성
const backupPath = configPath + '.backup-' + new Date().toISOString().split('T')[0];
fs.writeFileSync(backupPath, JSON.stringify(config, null, 2));
console.log('✅ 백업 생성:', backupPath);
console.log();

// 경로 변환 규칙
const pathMappings = {
    'K:/PortableApps/tools/nodejs/node.exe': 'K:/tools/nodejs/node.exe',
    'K:\\\\PortableApps\\\\tools\\\\nodejs\\\\node.exe': 'K:\\\\tools\\\\nodejs\\\\node.exe',
    'K:/PortableApps/tools/python/python.exe': 'K:/tools/python/python.exe',
