if (config.mcpServers) {
    console.log('MCP 서버 경로 업데이트:');
    console.log('-'.repeat(40));

    for (const [serverName, serverConfig] of Object.entries(config.mcpServers)) {
        let updated = false;

        // command 업데이트
        if (serverConfig.command) {
            for (const [oldPath, newPath] of Object.entries(pathMappings)) {
                if (serverConfig.command.includes(oldPath)) {
                    serverConfig.command = serverConfig.command.replace(oldPath, newPath);
                    updated = true;
                }
            }
        }

        // args 업데이트
        if (serverConfig.args && Array.isArray(serverConfig.args)) {
            serverConfig.args = serverConfig.args.map(arg => {
                let newArg = arg;
                for (const [oldPath, newPath] of Object.entries(pathMappings)) {
                    newArg = newArg.replace(oldPath, newPath);
                }
                return newArg;
            });
            if (JSON.stringify(serverConfig.args) !== JSON.stringify(config.mcpServers[serverName].args)) {
                updated = true;
            }
        }

        if (updated) {
            console.log(`✅ ${serverName}: 경로 업데이트 완료`);
            updateCount++;
        }
    }
}

// 파일 저장
fs.writeFileSync(configPath, JSON.stringify(config, null, 2));
