#!/usr/bin/env node
/**
 * MCP 서버 작동 테스트 스크립트
 * 19개 MCP 서버가 모두 정상 작동하는지 확인
 */

const { spawn } = require('child_process');
const fs = require('fs');
const path = require('path');

// Claude 설정 파일 읽기
const configPath = 'K:/PortableApps/Claude-Code/.claude.json';
const config = JSON.parse(fs.readFileSync(configPath, 'utf8'));

console.log('🔍 MCP 서버 검증 시작...\n');
console.log(`총 ${Object.keys(config.mcpServers).length}개 서버 발견\n`);

// 각 서버 정보 출력
Object.entries(config.mcpServers).forEach(([name, server], index) => {
    console.log(`${index + 1}. ${name}`);
    console.log(`   명령: ${server.command}`);
    console.log(`   경로: ${server.args ? server.args[0] : 'N/A'}`);

    // 경로 존재 여부 확인
    if (server.command && server.command.includes('K:/')) {
        const exists = fs.existsSync(server.command);
        console.log(`   ✅ 실행 파일: ${exists ? '존재' : '❌ 없음'}`);
    }

    // Node.js 모듈인 경우 파일 존재 확인
    if (server.args && server.args[0] && server.args[0].includes('K:\\')) {
        const modulePath = server.args[0].replace(/\\/g, '/');
        const exists = fs.existsSync(modulePath);
        console.log(`   ✅ 모듈 파일: ${exists ? '존재' : '❌ 없음'}`);
    }

    console.log('');
});

// K:/tools 경로 확인
console.log('📁 도구 경로 확인:');
const toolPaths = [
    'K:/tools/nodejs/node.exe',
    'K:/tools/nodejs/npm.cmd',
    'K:/tools/nodejs/npx.cmd',
    'K:/tools/python/python.exe',
    'K:/tools/git/bin/bash.exe'
];

toolPaths.forEach(toolPath => {
    const exists = fs.existsSync(toolPath);
    console.log(`   ${exists ? '✅' : '❌'} ${toolPath}`);
});

console.log('\n✨ 검증 완료!');

