#!/usr/bin/env node
/**
 * 19개 MCP 서버 상세 검증
 */

const fs = require('fs');
const path = require('path');

console.log('='.repeat(60));
console.log('   19개 MCP 서버 상세 검증');
console.log('='.repeat(60));
console.log('');

// .claude.json 읽기
const configPath = path.join(__dirname, '.claude.json');
const config = JSON.parse(fs.readFileSync(configPath, 'utf8'));

// MCP 서버 목록
const servers = Object.keys(config.mcpServers);
console.log(`📊 총 MCP 서버 수: ${servers.length}개`);
console.log('');

// 서버 목록 출력
console.log('📦 MCP 서버 목록:');
console.log('-'.repeat(40));
servers.forEach((name, index) => {
    console.log(`${(index + 1).toString().padStart(2)}. ${name}`);
});

console.log('');
console.log('📝 경로 분석:');
console.log('-'.repeat(40));

// 경로 문제 분석
let pathIssues = [];
let toolsPathCount = 0;
let portableAppsToolsCount = 0;

Object.entries(config.mcpServers).forEach(([name, server]) => {
    // K:/tools 경로 체크
    const commandStr = JSON.stringify(server);
    if (commandStr.includes('K:/tools/')) {
        toolsPathCount++;
    }
    if (commandStr.includes('K:/PortableApps/tools/')) {
        portableAppsToolsCount++;
        pathIssues.push({
            name: name,
            issue: 'K:/PortableApps/tools 경로 사용',
            command: server.command,
            args: server.args ? server.args[0] : null
        });
    }
});

console.log(`✅ K:/tools 경로 사용: ${toolsPathCount}개`);
console.log(`⚠️  K:/PortableApps/tools 경로 사용: ${portableAppsToolsCount}개`);

if (pathIssues.length > 0) {
    console.log('');
    console.log('🔧 수정 필요한 서버:');
    console.log('-'.repeat(40));
    pathIssues.forEach(issue => {
        console.log(`- ${issue.name}`);
        console.log(`  현재: ${issue.command}`);
        console.log(`  변경: ${issue.command.replace('K:/PortableApps/tools/', 'K:/tools/')}`);
    });
}

// 결과 요약
console.log('');
console.log('='.repeat(60));
if (servers.length === 19) {
    console.log('✅ 19개 MCP 서버 확인 완료!');
} else {
    console.log(`⚠️  서버 수 불일치: ${servers.length}개 (19개여야 함)`);
}

if (portableAppsToolsCount > 0) {
    console.log(`⚠️  ${portableAppsToolsCount}개 서버가 K:/PortableApps/tools 경로 사용 중`);
    console.log('   → K:/tools로 마이그레이션 필요');
}

console.log('='.repeat(60));

