const fs = require('fs');

// .claude.json 파일 읽기
const config = JSON.parse(fs.readFileSync('.claude.json', 'utf8'));
const servers = config.mcpServers;
const serverNames = Object.keys(servers);

console.log('\n=== 19개 MCP 서버 분석 결과 ===\n');
