console.log('서버 목록:');
serverNames.forEach((name, i) => {
    console.log(`  ${i + 1}. ${name}`);
});

// 경로 분석
console.log('\n경로 사용 현황:');
let toolsPath = 0;
let portableAppsTools = 0;
let needsFix = [];

Object.entries(servers).forEach(([name, server]) => {
    const str = JSON.stringify(server);
    if (str.includes('K:/tools/')) {
        toolsPath++;
    }
    if (str.includes('K:/PortableApps/tools/') || str.includes('K:\\\\PortableApps\\\\tools\\\\')) {
        portableAppsTools++;
        needsFix.push(name);
    }
});

console.log(`  K:/tools 사용: ${toolsPath}개`);
console.log(`  K:/PortableApps/tools 사용: ${portableAppsTools}개`);

if (needsFix.length > 0) {
    console.log('\n수정 필요한 서버:');
    needsFix.forEach(name => {
        console.log(`  - ${name}`);
    });
}

// 검증 결과
console.log('\n=== 검증 결과 ===');
if (serverNames.length === 19) {
    console.log('✅ 19개 서버 모두 확인됨');
} else {
    console.log(`⚠️ 서버 수 불일치: ${serverNames.length}개`);
}

if (portableAppsTools > 0) {
    console.log(`⚠️ ${portableAppsTools}개 서버 경로 수정 필요`);
}

// 파일로 저장
const report = {
