# PowerShell Main Environment for Claude Code
# Bash 문제를 완전히 우회하는 메인 실행 환경

Write-Host "`n🚀 PowerShell 메인 환경 구축 중..." -ForegroundColor Cyan
Write-Host "================================" -ForegroundColor Cyan

# 1. 환경 변수 설정
$env:CLAUDE_HOME = "K:\PortableApps\Claude-Code"
$env:NODE_PATH = "K:\PortableApps\tools\nodejs"
$env:PYTHON_PATH = "K:\PortableApps\tools\python" 
$env:GIT_PATH = "K:\PortableApps\tools\git"
$env:CLAUDE_CODE_GIT_BASH_PATH = "$env:GIT_PATH\bin\bash.exe"

# PATH 업데이트
$env:PATH = "$env:NODE_PATH;$env:PYTHON_PATH;$env:GIT_PATH\bin;$env:PATH"

Write-Host "`n✅ 환경 변수 설정 완료" -ForegroundColor Green
Write-Host "  NODE_PATH: $env:NODE_PATH" -ForegroundColor Gray
Write-Host "  PYTHON_PATH: $env:PYTHON_PATH" -ForegroundColor Gray
Write-Host "  GIT_PATH: $env:GIT_PATH" -ForegroundColor Gray

# 2. 도구 존재 확인
Write-Host "`n🔍 도구 확인 중..." -ForegroundColor Yellow
$tools = @{
    "Node.js" = "$env:NODE_PATH\node.exe"
    "Python" = "$env:PYTHON_PATH\python.exe"
    "Git Bash" = "$env:GIT_PATH\bin\bash.exe"
}

foreach ($tool in $tools.GetEnumerator()) {
    if (Test-Path $tool.Value) {
        Write-Host "  ✅ $($tool.Key): 발견" -ForegroundColor Green
    } else {
        Write-Host "  ❌ $($tool.Key): 없음 - $($tool.Value)" -ForegroundColor Red
    }
}

# 3. Claude 함수 정의
function claude {
    param([Parameter(ValueFromRemainingArguments=$true)]$args)

    $claudeCLI = "$env:CLAUDE_HOME\node_modules\@anthropic-ai\claude-code\cli.js"
    if (Test-Path $claudeCLI) {
        & "$env:NODE_PATH\node.exe" $claudeCLI $args
    } else {
        Write-Host "❌ Claude CLI를 찾을 수 없습니다: $claudeCLI" -ForegroundColor Red
    }
}

# 4. MCP 서버 상태 확인
function Check-MCP {
    Write-Host "`n📦 MCP 서버 확인..." -ForegroundColor Cyan

    $configFile = "$env:CLAUDE_HOME\.claude.json"
    if (Test-Path $configFile) {
        $config = Get-Content $configFile | ConvertFrom-Json
        $serverCount = ($config.mcpServers | Get-Member -MemberType NoteProperty).Count
        Write-Host "  총 $serverCount개 MCP 서버 설정됨" -ForegroundColor Green

        if ($serverCount -eq 19) {
            Write-Host "  ✅ 19개 서버 모두 확인!" -ForegroundColor Green
        } else {
            Write-Host "  ⚠️ 서버 수 불일치 (19개여야 함)" -ForegroundColor Yellow
        }
    }
}

# 5. Bash 문제 진단
function Diagnose-Bash {
    Write-Host "`n🔧 Bash 문제 진단..." -ForegroundColor Cyan

    # shell-snapshots 디렉토리 확인
    $snapshotDir = "$env:CLAUDE_HOME\shell-snapshots"
    if (Test-Path $snapshotDir) {
        $files = Get-ChildItem $snapshotDir -Filter "*.sh" 2>$null
        if ($files) {
            Write-Host "  ⚠️ 문제가 되는 snapshot 파일 발견:" -ForegroundColor Yellow
            foreach ($file in $files) {
                Write-Host "    - $($file.Name)" -ForegroundColor Gray
            }
            Write-Host "  💡 해결: PowerShell 환경에서 이 파일들을 제거하면 됩니다" -ForegroundColor Cyan
        }
    }

    # WSL 경로 확인
    Write-Host "`n  WSL 경로 문제:" -ForegroundColor Yellow
    Write-Host "    - /k/ → K:\" -ForegroundColor Gray
    Write-Host "    - /tmp/ → $env:TEMP\" -ForegroundColor Gray
    Write-Host "  💡 해결: Windows 네이티브 경로 사용" -ForegroundColor Cyan
}

# 6. 빠른 명령어
Write-Host "`n📌 사용 가능한 명령어:" -ForegroundColor Cyan
Write-Host "  claude --version     - Claude 버전 확인" -ForegroundColor White
Write-Host "  claude mcp list      - MCP 서버 목록" -ForegroundColor White
Write-Host "  claude doctor        - 시스템 진단" -ForegroundColor White
Write-Host "  Check-MCP           - MCP 서버 상태 확인" -ForegroundColor White
Write-Host "  Diagnose-Bash       - Bash 문제 진단" -ForegroundColor White

# 초기 진단 실행
Check-MCP
Diagnose-Bash

Write-Host "`n✨ PowerShell 환경 준비 완료!" -ForegroundColor Green
Write-Host "이제 'claude' 명령을 사용할 수 있습니다." -ForegroundColor Yellow

@echo off
REM PowerShell 메인 환경 시작
REM Bash 문제를 완전히 우회하는 안정적인 실행 환경

echo.
echo ========================================
echo    Claude Code PowerShell Environment
echo ========================================
echo.

REM PowerShell 실행 (프로파일 로드 후 대화형 모드)
powershell -NoExit -ExecutionPolicy Bypass -Command "& { . 'K:\PortableApps\Claude-Code\POWERSHELL-MAIN.ps1' }"

# PowerShell 환경 완전 테스트
Write-Host "`n🧪 PowerShell 환경 테스트 시작" -ForegroundColor Cyan
Write-Host "================================" -ForegroundColor Cyan

# 1. 기본 도구 테스트
Write-Host "`n1️⃣ 기본 도구 확인" -ForegroundColor Yellow
$tests = @{
    "Node.js" = @{
        Path = "K:\PortableApps\tools\nodejs\node.exe"
        Test = "--version"
    }
    "Python" = @{
        Path = "K:\PortableApps\tools\python\python.exe"
        Test = "--version"
    }
    "Git" = @{
        Path = "K:\PortableApps\tools\git\bin\git.exe"
        Test = "--version"
    }
}

foreach ($tool in $tests.GetEnumerator()) {
    if (Test-Path $tool.Value.Path) {
        $result = & $tool.Value.Path $tool.Value.Test 2>&1 | Out-String
        Write-Host "✅ $($tool.Key): $($result.Trim())" -ForegroundColor Green
    } else {
        Write-Host "❌ $($tool.Key): 찾을 수 없음" -ForegroundColor Red
    }
}

# 2. Claude CLI 테스트
Write-Host "`n2️⃣ Claude CLI 테스트" -ForegroundColor Yellow
$claudeCLI = "K:\PortableApps\Claude-Code\node_modules\@anthropic-ai\claude-code\cli.js"
$nodeExe = "K:\PortableApps\tools\nodejs\node.exe"

if ((Test-Path $claudeCLI) -and (Test-Path $nodeExe)) {
    # 버전 확인
    $version = & $nodeExe $claudeCLI --version 2>&1
    Write-Host "✅ Claude 버전: $version" -ForegroundColor Green

    # MCP 서버 수 확인
    Write-Host "`n3️⃣ MCP 서버 확인" -ForegroundColor Yellow
    $config = Get-Content "K:\PortableApps\Claude-Code\.claude.json" | ConvertFrom-Json
    $serverCount = ($config.mcpServers | Get-Member -MemberType NoteProperty).Count
    Write-Host "✅ MCP 서버: $serverCount개" -ForegroundColor Green

    if ($serverCount -eq 19) {
        Write-Host "   ✨ 19개 서버 모두 정상!" -ForegroundColor Green
    }
} else {
    Write-Host "❌ Claude CLI를 찾을 수 없음" -ForegroundColor Red
}

# 3. Bash 문제 상태
Write-Host "`n4️⃣ Bash 문제 진단" -ForegroundColor Yellow
$snapshotDir = "K:\PortableApps\Claude-Code\shell-snapshots"
if (Test-Path $snapshotDir) {
    $files = Get-ChildItem $snapshotDir -Filter "*.sh" -ErrorAction SilentlyContinue
    if ($files) {
        Write-Host "⚠️ 문제가 되는 snapshot 파일 $($files.Count)개 발견" -ForegroundColor Yellow
    } else {
        Write-Host "✅ shell-snapshots 디렉토리 깨끗함" -ForegroundColor Green
    }
} else {
    Write-Host "✅ shell-snapshots 디렉토리 없음 (정상)" -ForegroundColor Green
}

Write-Host "`n✨ 테스트 완료!" -ForegroundColor Green
Write-Host "`n💡 다음 단계:" -ForegroundColor Cyan
Write-Host "  1. PowerShell에서 작업 계속" -ForegroundColor White
Write-Host "  2. '.\FIX-BASH-FROM-POWERSHELL.ps1 -Fix' 실행하여 Bash 문제 해결" -ForegroundColor White

#!/usr/bin/env node
/**
 * 모든 문제를 한 번에 해결하는 단일 스크립트
 * 배치 파일 없이 Node.js로 직접 실행
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

console.log('\n🔥 모든 문제를 한 번에 해결합니다\n');

// 1. shell-snapshots 문제 해결
console.log('1️⃣ shell-snapshots 오류 제거...');
const snapshotDir = path.join(__dirname, 'shell-snapshots');
if (fs.existsSync(snapshotDir)) {
    const files = fs.readdirSync(snapshotDir).filter(f => f.endsWith('.sh'));
    if (files.length > 0) {
        const archiveDir = path.join(__dirname, 'ARCHIVE', `shell-snapshots-${Date.now()}`);
        fs.mkdirSync(archiveDir, { recursive: true });
        files.forEach(file => {
            fs.renameSync(
                path.join(snapshotDir, file),
                path.join(archiveDir, file)
            );
        });
        console.log(`   ✅ ${files.length}개 파일 백업 완료`);
    }
}

// 2. .claude.json 경로 통일 (K:/tools로)
console.log('\n2️⃣ 모든 경로를 K:/tools로 통일...');
const configPath = path.join(__dirname, '.claude.json');
const config = JSON.parse(fs.readFileSync(configPath, 'utf8'));

// 경로 변경 함수
function fixPaths(obj) {
    if (typeof obj === 'string') {
        return obj
            .replace(/K:[\\\/]PortableApps[\\\/]tools/g, 'K:/tools')
            .replace(/K:\\\\PortableApps\\\\tools/g, 'K:\\\\tools');
    }
    if (Array.isArray(obj)) {
        return obj.map(fixPaths);
    }
    if (obj && typeof obj === 'object') {
        const result = {};
        for (const key in obj) {
            result[key] = fixPaths(obj[key]);
        }
        return result;
    }
    return obj;
}

// 모든 MCP 서버 경로 수정
config.mcpServers = fixPaths(config.mcpServers);

// 백업 생성
const backupPath = configPath + `.backup-${Date.now()}`;
fs.copyFileSync(configPath, backupPath);
console.log(`   📁 백업: ${backupPath}`);

// 저장
fs.writeFileSync(configPath, JSON.stringify(config, null, 2));
console.log('   ✅ .claude.json 경로 통일 완료');

// 3. MCP 서버 검증
console.log('\n3️⃣ 19개 MCP 서버 검증...');
const serverNames = Object.keys(config.mcpServers);
console.log(`   총 ${serverNames.length}개 서버:`);
serverNames.forEach((name, i) => {
    console.log(`   ${(i+1).toString().padStart(2)}. ${name}`);
});

if (serverNames.length === 19) {
    console.log('   ✅ 19개 서버 모두 확인!');
} else {
    console.log(`   ⚠️ 서버 수 불일치: ${serverNames.length}개 (19개여야 함)`);
}

// 4. 실제 Claude 실행 테스트
console.log('\n4️⃣ Claude 실행 테스트...');
const claudeCLI = path.join(__dirname, 'node_modules', '@anthropic-ai', 'claude-code', 'cli.js');
const nodeExe = 'K:\\PortableApps\\tools\\nodejs\\node.exe';

if (fs.existsSync(claudeCLI) && fs.existsSync(nodeExe)) {
    try {
        const version = execSync(`"${nodeExe}" "${claudeCLI}" --version`, { encoding: 'utf8' }).trim();
        console.log(`   ✅ Claude 버전: ${version}`);
    } catch (error) {
        console.log(`   ❌ 실행 실패: ${error.message}`);
    }
} else {
    console.log('   ❌ Claude CLI 또는 Node.js를 찾을 수 없음');
}

// 5. 단일 실행 스크립트 생성
console.log('\n5️⃣ 단일 실행 스크립트 생성...');
const runScript = `@echo off
REM 단일 Claude 실행 스크립트
REM 모든 환경 변수와 경로 설정 포함

set CLAUDE_HOME=K:\\PortableApps\\Claude-Code
set NODE_PATH=K:\\tools\\nodejs
set PYTHON_PATH=K:\\tools\\python
set GIT_PATH=K:\\tools\\git
set CLAUDE_CODE_GIT_BASH_PATH=%GIT_PATH%\\bin\\bash.exe
set CLAUDE_DISABLE_SNAPSHOTS=1
set PATH=%NODE_PATH%;%PYTHON_PATH%;%GIT_PATH%\\bin;%PATH%

REM Claude 실행
%NODE_PATH%\\node.exe %CLAUDE_HOME%\\node_modules\\@anthropic-ai\\claude-code\\cli.js %*
`;

fs.writeFileSync(path.join(__dirname, 'CLAUDE.bat'), runScript);
console.log('   ✅ CLAUDE.bat 생성 완료');

// 6. 불필요한 배치 파일 정리
console.log('\n6️⃣ 불필요한 파일 정리...');
const unnecessaryFiles = [
    'POWERSHELL-EXECUTOR.ps1',
    'claude-ps.bat', 
    'FIX-BASH-WITH-POWERSHELL.ps1',
    'START-POWERSHELL.bat',
    'POWERSHELL-MAIN.ps1',
    'FIX-BASH-FROM-POWERSHELL.ps1',
    'TEST-POWERSHELL-ENV.ps1',
    'START-CLAUDE-PS.bat'
];

const cleanupDir = path.join(__dirname, 'ARCHIVE', 'old-batch-files');
fs.mkdirSync(cleanupDir, { recursive: true });

let cleaned = 0;
unnecessaryFiles.forEach(file => {
    const filePath = path.join(__dirname, file);
    if (fs.existsSync(filePath)) {
        fs.renameSync(filePath, path.join(cleanupDir, file));
        cleaned++;
    }
});
console.log(`   ✅ ${cleaned}개 파일 정리 완료`);

// 완료
console.log('\n' + '='.repeat(50));
console.log('✨ 모든 문제 해결 완료!');
console.log('='.repeat(50));
console.log('\n사용법:');
console.log('  CLAUDE.bat --version    # 버전 확인');
console.log('  CLAUDE.bat mcp list     # MCP 서버 목록');
console.log('  CLAUDE.bat              # Claude 시작');
console.log('\n하나의 CLAUDE.bat 파일로 모든 것이 작동합니다.');

