#!/usr/bin/env node
/**
 * 모든 쓸모없는 배치 파일과 테스트 파일 정리
 */

const fs = require('fs');
const path = require('path');

const junkFiles = [
    // 모든 FIX 파일들
    'FIX-*.bat', 'FIX-*.js', 'FIX-*.ps1',
    // 모든 TEST 파일들  
    'TEST-*.bat', 'TEST-*.js', 'TEST-*.ps1',
    // 모든 VERIFY 파일들
    'VERIFY-*.bat', 'VERIFY-*.js',
    // 모든 CHECK 파일들
    'CHECK-*.bat', 'CHECK-*.js',
    // PowerShell 관련
    'POWERSHELL-*.ps1', 'START-POWERSHELL.bat',
    // 기타 불필요한 것들
    'ANALYZE-*.js', 'DETECT-*.js', 'MONITOR-*.js',
    'RUN-*.bat', 'RUN-*.ps1',
    'claude-direct.bat', 'claude-fixed.bat', 'claude-optimized.bat',
    'claude-ps.bat', 'claude-k-tools.bat',
    'ULTIMATE-*.bat', 'EMERGENCY-*.bat', 'FORCE-*.bat',
    'CLEANUP-*.txt', 'EVIDENCE-*.txt', 'DOCS-*.txt'
];

console.log('🧹 불필요한 파일 정리 시작...\n');

const archiveDir = path.join(__dirname, 'ARCHIVE', 'old-junk-files');
fs.mkdirSync(archiveDir, { recursive: true });

let cleaned = 0;
const files = fs.readdirSync(__dirname);

files.forEach(file => {
    // 패턴 매칭
    for (const pattern of junkFiles) {
        const regex = new RegExp('^' + pattern.replace('*', '.*') + '$');
        if (regex.test(file)) {
            const src = path.join(__dirname, file);
            const dest = path.join(archiveDir, file);

            if (fs.statSync(src).isFile()) {
                fs.renameSync(src, dest);
                console.log(`  이동: ${file}`);
                cleaned++;
                break;
            }
        }
    }
});

console.log(`\n✅ ${cleaned}개 파일 정리 완료`);
console.log(`📁 보관 위치: ${archiveDir}`);

// 최종 확인
console.log('\n🎯 남은 중요 파일:');
console.log('  - CLAUDE-FINAL.bat (메인 실행 파일)');
console.log('  - .claude.json (설정 파일)');
console.log('  - CLAUDE.md (지침 파일)');
console.log('  - node_modules/ (의존성)');
console.log('  - mcp-servers/ (MCP 서버)');
console.log('  - ShrimpData/ (작업 데이터)');
console.log('\n이제 CLAUDE-FINAL.bat 하나로 모든 것이 작동합니다!');
