function findCleanupFiles(dir) {
    try {
        const items = fs.readdirSync(dir);
        for (const item of items) {
            const fullPath = path.join(dir, item);

            // ì œì™¸ ë””ë ‰í† ë¦¬
            if (item === 'node_modules' || item === '.git' || item === 'tools') continue;

            for (const { pattern, desc } of patterns) {
