const baseDir = 'K:/PortableApps/Claude-Code';

targetFiles.forEach(file => {
    const fullPath = path.join(baseDir, file);

    if (!fs.existsSync(fullPath)) {
        console.log(`[-] File not found: ${file}`);
        return;
    }

    // 백업 생성
    const backupPath = fullPath + '.bilingual-backup-' + Date.now();
    fs.copyFileSync(fullPath, backupPath);

    // 파일 읽기
    let content = fs.readFileSync(fullPath, 'utf8');
    let fileChanges = 0;

    // 바이링구얼 매핑 적용
    Object.entries(bilingualMap).forEach(([original, bilingual]) => {
        const regex = new RegExp(original.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'g');
        const matches = content.match(regex);

        if (matches) {
            content = content.replace(regex, bilingual);
            fileChanges += matches.length;
        }
    });

    if (fileChanges > 0) {
        fs.writeFileSync(fullPath, content, 'utf8');
        console.log(`[+] ${path.basename(file)}: ${fileChanges} changes applied`);
        totalChanges += fileChanges;
    } else {
        // 변경사항 없으면 백업 제거
        fs.unlinkSync(backupPath);
        console.log(`[=] ${path.basename(file)}: No changes needed`);
    }
});

console.log(`\n[*] Bilingual patch complete! Total ${totalChanges} items updated`);

