    ║  - MinIO/S3 (바이너리 저장)                                                 ║
    ║  - Redis (캐싱)                                                            ║
    ║                                                                              ║
    ║  모든 데이터는 실제 위협 정보 기반 샘플입니다                                ║
    ╚══════════════════════════════════════════════════════════════════════════════╝
    -->

    <style>
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        :root {
            /* Quantum Color Palette */
            --quantum-blue: #00a8ff;
            --quantum-purple: #7c3aed;
            --quantum-teal: #14b8a6;
            --quantum-indigo: #4f46e5;
            --quantum-cyan: #06b6d4;

            /* Alert Severity Colors */
            --critical: #dc2626;
            --high: #ea580c;
            --medium: #f59e0b;
            --low: #84cc16;
            --info: #0ea5e9;

            /* Dark Theme */
            --dark-primary: #0a0a0f;
            --dark-secondary: #12121a;
            --dark-tertiary: #1a1a25;
            --dark-surface: #22222f;

            /* Glass Effect */
            --glass: rgba(255, 255, 255, 0.03);
            --glass-hover: rgba(255, 255, 255, 0.05);
            --glass-border: rgba(255, 255, 255, 0.08);
            --glass-active: rgba(0, 168, 255, 0.1);

            /* Text */
            --text-primary: #ffffff;
            --text-secondary: #94a3b8;
            --text-dim: #64748b;
            --text-muted: #475569;
        }

        body {
            font-family: -apple-system, 'SF Pro Display', BlinkMacSystemFont, 'Inter', 'Segoe UI', sans-serif;
            background: linear-gradient(135deg, #0a0a0f 0%, #12121a 25%, #0a0a0f 50%, #12121a 75%, #0a0a0f 100%);
            color: var(--text-primary);
            overflow: hidden;
            position: relative;
            height: 100vh;
        }

        /* xAI Insight Panel Styles */
        .xai-panel {
            position: fixed;
            top: 80px;
            right: 20px;
            width: 350px;
            max-height: 600px;
            background: linear-gradient(135deg, rgba(0, 168, 255, 0.1), rgba(124, 58, 237, 0.05));
            backdrop-filter: blur(20px);
            border: 1px solid var(--glass-border);
            border-radius: 12px;
            padding: 20px;
            z-index: 10000;
            transform: translateX(400px);
            transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1);
            overflow-y: auto;
            box-shadow: 0 20px 60px rgba(0, 168, 255, 0.2);
        }

        .xai-panel.active {
            transform: translateX(0);
        }

        .xai-panel h3 {
            font-size: 16px;
            margin-bottom: 15px;
            color: var(--quantum-blue);
            display: flex;
            align-items: center;
            gap: 8px;
        }

        .xai-close {
            position: absolute;
            top: 15px;
            right: 15px;
            width: 24px;
            height: 24px;
            border-radius: 50%;
            background: rgba(255, 255, 255, 0.1);
            border: none;
            color: var(--text-dim);
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
            transition: all 0.2s;
        }

        .xai-close:hover {
            background: rgba(220, 38, 38, 0.3);
            color: #dc2626;
        }

        .xai-section {
            margin-bottom: 20px;
            padding: 12px;
            background: var(--dark-tertiary);
            border-radius: 8px;
            border-left: 3px solid var(--quantum-blue);
        }

        .xai-metric-explain {
            font-size: 12px;
            line-height: 1.6;
            color: var(--text-secondary);
        }

        .xai-insight {
            margin-top: 10px;
            padding: 10px;
            background: rgba(0, 168, 255, 0.1);
            border-radius: 6px;
            font-size: 11px;
        }

        .xai-recommendation {
            margin-top: 10px;
            padding: 10px;
            background: rgba(132, 204, 22, 0.1);
            border: 1px solid #84cc16;
            border-radius: 6px;
            font-size: 11px;
        }

        .xai-risk {
            margin-top: 10px;
            padding: 10px;
            background: rgba(220, 38, 38, 0.1);
            border: 1px solid #dc2626;
            border-radius: 6px;
            font-size: 11px;
        }

        .xai-toggle {
            display: flex;
            gap: 10px;
            margin-bottom: 15px;
        }

        .xai-toggle button {
            flex: 1;
            padding: 6px;
            background: var(--dark-surface);
            border: 1px solid var(--glass-border);
            border-radius: 6px;
            color: var(--text-dim);
            font-size: 11px;
            cursor: pointer;
            transition: all 0.2s;
        }

        .xai-toggle button.active {
            background: var(--quantum-blue);
            color: white;
            border-color: var(--quantum-blue);
        }

        .clickable-metric {
            cursor: pointer;
            transition: all 0.2s;
            position: relative;
        }

        .clickable-metric:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(0, 168, 255, 0.3);
        }

        .clickable-metric::after {
            content: "ℹ";
            position: absolute;
            top: 5px;
            right: 5px;
            width: 16px;
            height: 16px;
            border-radius: 50%;
            background: rgba(0, 168, 255, 0.3);
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 10px;
            opacity: 0;
            transition: opacity 0.2s;
        }

        .clickable-metric:hover::after {
            opacity: 1;
        }

        /* Animated Background Grid */
        body::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-image: 
                linear-gradient(rgba(0, 168, 255, 0.03) 1px, transparent 1px),
                linear-gradient(90deg, rgba(0, 168, 255, 0.03) 1px, transparent 1px);
            background-size: 50px 50px;
            animation: gridMove 20s linear infinite;
            pointer-events: none;
        }

        @keyframes gridMove {
            0% { transform: translate(0, 0); }
            100% { transform: translate(50px, 50px); }
        }

        /* Main Layout - Optimized Grid */
        .atip-container {
            display: grid;
            grid-template-columns: 70px 260px 1fr 380px;
            grid-template-rows: 1fr;
            height: 100vh;
            position: relative;
            z-index: 1;
        }

        /* Neural Command Center */
        .neural-command {
            background: linear-gradient(180deg, var(--dark-primary) 0%, var(--dark-secondary) 100%);
            border-right: 1px solid var(--glass-border);
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 15px 0;
            gap: 12px;
            position: relative;
        }

        .neural-command::after {
            content: '';
            position: absolute;
            right: 0;
            top: 0;
            width: 1px;
            height: 100%;
            background: linear-gradient(180deg, 
                transparent 0%, 
                var(--quantum-blue) 50%, 
                transparent 100%);
            animation: scanLine 3s ease-in-out infinite;
        }

        @keyframes scanLine {
            0%, 100% { opacity: 0; }
            50% { opacity: 1; }
        }

        .atip-logo {
            width: 48px;
            height: 48px;
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
            border-radius: 14px;
            display: flex;
            align-items: center;
            justify-content: center;
            font-weight: 900;
            font-size: 11px;
            color: white;
            cursor: pointer;
            position: relative;
            box-shadow: 0 0 20px rgba(0, 168, 255, 0.3);
            transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }

        .atip-logo:hover {
            transform: scale(1.05);
            box-shadow: 0 0 30px rgba(124, 58, 237, 0.5);
        }

        @keyframes logoRotate {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        .neural-module {
            width: 48px;
            height: 48px;
            border-radius: 10px;
            background: var(--glass);
            border: 1px solid var(--glass-border);
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            cursor: pointer;
            transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
            position: relative;
        }

        .neural-module-icon {
            font-size: 20px;
            margin-bottom: 2px;
        }

        .neural-module-label {
            font-size: 8px;
            font-weight: 600;
            text-transform: uppercase;
            letter-spacing: 0.5px;
        }

        .neural-module:hover {
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
            transform: translateX(5px) scale(1.05);
            box-shadow: 0 0 25px rgba(0, 168, 255, 0.6);
        }

        .neural-module.active {
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
            box-shadow: 0 0 30px rgba(124, 58, 237, 0.6);
        }

        .neural-module.active::before {
            content: '';
            position: absolute;
            inset: -2px;
            border-radius: 12px;
            padding: 2px;
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
            -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
            -webkit-mask-composite: xor;
            mask-composite: exclude;
            animation: borderRotate 3s linear infinite;
        }

        @keyframes borderRotate {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        .module-alert {
            position: absolute;
            top: -4px;
            right: -4px;
            min-width: 18px;
            height: 18px;
            background: var(--critical);
            color: white;
            font-size: 10px;
            font-weight: bold;
            padding: 0 4px;
            border-radius: 9px;
            display: flex;
            align-items: center;
            justify-content: center;
            animation: alertPulse 1s ease-in-out infinite;
        }

        @keyframes alertPulse {
            0%, 100% { transform: scale(1); }
            50% { transform: scale(1.1); }
        }

        /* Intelligence Navigator */
        .intel-navigator {
            background: rgba(10, 10, 15, 0.95);
            backdrop-filter: blur(20px);
            border-right: 1px solid var(--glass-border);
            overflow-y: auto;
            overflow-x: hidden;
        }

        .nav-header {
            padding: 20px;
            background: linear-gradient(180deg, var(--dark-secondary) 0%, transparent 100%);
            border-bottom: 1px solid var(--glass-border);
            position: sticky;
            top: 0;
            z-index: 10;
        }

        .nav-title {
            font-size: 14px;
            font-weight: 700;
            margin-bottom: 15px;
            background: linear-gradient(135deg, var(--text-primary), var(--quantum-blue));
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
        }

        /* Real-time Metrics Grid */
        .metrics-grid {
            display: grid;
            grid-template-columns: repeat(2, 1fr);
            gap: 8px;
        }

        .metric-card {
            background: var(--glass);
            border: 1px solid var(--glass-border);
            border-radius: 8px;
            padding: 10px;
            position: relative;
            overflow: hidden;
        }

        .metric-card::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 2px;
            background: linear-gradient(90deg, 
                transparent, 
                var(--quantum-blue), 
                transparent);
            animation: scanMetric 2s linear infinite;
        }

        @keyframes scanMetric {
            0% { transform: translateX(-100%); }
            100% { transform: translateX(100%); }
        }

        .metric-value {
            font-size: 20px;
            font-weight: bold;
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-teal));
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
        }

        .metric-label {
            font-size: 9px;
            color: var(--text-dim);
            text-transform: uppercase;
            margin-top: 4px;
            letter-spacing: 0.5px;
        }

        .metric-trend {
            position: absolute;
            top: 10px;
            right: 10px;
            font-size: 10px;
        }

        .metric-trend.up {
            color: var(--low);
        }

        .metric-trend.down {
            color: var(--critical);
        }

        /* Navigation Sections */
        .nav-section {
            margin-bottom: 5px;
        }

        .nav-section-header {
            padding: 10px 20px;
            font-size: 10px;
            text-transform: uppercase;
            letter-spacing: 1px;
            color: var(--text-dim);
            background: var(--dark-tertiary);
            display: flex;
            justify-content: space-between;
            align-items: center;
            cursor: pointer;
        }

        .nav-section-status {
            display: flex;
            align-items: center;
            gap: 5px;
        }

        .status-indicator {
            width: 6px;
            height: 6px;
            border-radius: 50%;
            animation: statusPulse 2s ease-in-out infinite;
        }

        .status-indicator.active {
            background: var(--low);
        }

        .status-indicator.warning {
            background: var(--medium);
        }

        .status-indicator.critical {
            background: var(--critical);
        }

        @keyframes statusPulse {
            0%, 100% { opacity: 1; transform: scale(1); }
            50% { opacity: 0.6; transform: scale(0.8); }
        }

        .nav-item {
            padding: 12px 20px;
            cursor: pointer;
            transition: all 0.2s;
            display: flex;
            align-items: center;
            justify-content: space-between;
            position: relative;
            font-size: 12px;
            border-left: 3px solid transparent;
        }

        .nav-item:hover {
            background: var(--glass-hover);
            padding-left: 24px;
            border-left-color: var(--quantum-blue);
        }

        .nav-item.active {
            background: linear-gradient(90deg, rgba(0, 168, 255, 0.1) 0%, transparent 100%);
            border-left-color: var(--quantum-purple);
        }

        .nav-item-content {
            flex: 1;
        }

        .nav-item-label {
            display: block;
            margin-bottom: 2px;
        }

        .nav-item-meta {
            font-size: 10px;
            color: var(--text-muted);
        }

        .nav-item-badge {
            background: var(--glass);
            border: 1px solid var(--glass-border);
            padding: 2px 8px;
            border-radius: 10px;
            font-size: 10px;
            font-weight: 600;
        }

        .nav-item-badge.new {
            background: var(--quantum-blue);
            border-color: var(--quantum-blue);
            color: white;
        }

        .nav-item-badge.dev {
            background: var(--medium);
            border-color: var(--medium);
            color: var(--dark-primary);
        }

        /* Main Intelligence Canvas */
        .intel-canvas {
            background: var(--dark-primary);
            overflow-y: auto;
            position: relative;
        }

        /* Platform Header */
        .platform-header {
            background: linear-gradient(180deg, rgba(18, 18, 26, 0.95) 0%, transparent 100%);
            backdrop-filter: blur(20px);
            padding: 20px 30px;
            border-bottom: 1px solid var(--glass-border);
            position: sticky;
            top: 0;
            z-index: 100;
        }

        .header-top {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 20px;
        }

        .page-title-section {
            flex: 1;
        }

        .page-title {
            font-size: 24px;
            font-weight: 700;
            background: linear-gradient(135deg, white, var(--quantum-blue));
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            margin-bottom: 5px;
        }

        .page-subtitle {
            font-size: 12px;
            color: var(--text-secondary);
        }

        .header-actions {
            display: flex;
            gap: 10px;
        }

        .action-btn {
            padding: 8px 16px;
            background: var(--glass);
            border: 1px solid var(--glass-border);
            border-radius: 8px;
            color: white;
            cursor: pointer;
            font-size: 12px;
            font-weight: 500;
            transition: all 0.3s;
            display: flex;
            align-items: center;
            gap: 6px;
            white-space: nowrap;
        }

        .action-btn:hover {
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
            transform: translateY(-2px);
            box-shadow: 0 10px 30px rgba(0, 168, 255, 0.3);
        }

        .action-btn.primary {
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
        }

        /* Threat Level Indicator Bar */
        .threat-level-bar {
            background: var(--glass);
            border: 1px solid var(--glass-border);
            border-radius: 10px;
            padding: 10px 15px;
            display: grid;
            grid-template-columns: repeat(5, 1fr);
            gap: 15px;
        }

        .threat-level-item {
            display: flex;
            align-items: center;
            gap: 8px;
            font-size: 11px;
        }

        .threat-level-indicator {
            width: 8px;
            height: 8px;
            border-radius: 50%;
            animation: pulse 2s infinite;
        }

        .threat-level-label {
            color: var(--text-secondary);
        }

        .threat-level-value {
            margin-left: auto;
            font-weight: 600;
        }

        /* Content Area */
        .content-area {
            padding: 25px;
        }

        /* Advanced Knowledge Graph */
        .knowledge-graph-advanced {
            background: var(--glass);
            border: 1px solid var(--glass-border);
            border-radius: 16px;
            padding: 20px;
            margin-bottom: 25px;
            position: relative;
            height: 550px;
        }

        .graph-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 20px;
        }

        .graph-title {
            font-size: 16px;
            font-weight: 600;
        }

        .graph-controls {
            display: flex;
            gap: 8px;
        }

        .graph-btn {
            padding: 6px 12px;
            background: var(--dark-tertiary);
            border: 1px solid var(--glass-border);
            border-radius: 6px;
            color: white;
            cursor: pointer;
            font-size: 11px;
            transition: all 0.2s;
        }

        .graph-btn:hover {
            background: var(--quantum-blue);
            transform: translateY(-1px);
        }

        .graph-btn.active {
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
        }

        #threatGraph {
            width: 100%;
            height: calc(100% - 60px);
            position: relative;
        }

        /* Detection Pipeline Visualization */
        .detection-pipeline {
            background: var(--glass);
            border: 1px solid var(--glass-border);
            border-radius: 16px;
            padding: 20px;
            margin-bottom: 25px;
        }

        .pipeline-stages {
            display: flex;
            justify-content: space-between;
            align-items: center;
            position: relative;
            padding: 20px 0;
        }

        .pipeline-connector {
            position: absolute;
            top: 50%;
            left: 0;
            right: 0;
            height: 2px;
            background: linear-gradient(90deg,
                var(--low) 0%,
                var(--quantum-blue) 50%,
                var(--glass-border) 100%);
            z-index: 1;
        }

        .pipeline-stage {
            flex: 1;
            text-align: center;
            position: relative;
            z-index: 2;
        }

        .stage-icon {
            width: 56px;
            height: 56px;
            background: var(--dark-tertiary);
            border: 2px solid var(--glass-border);
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            margin: 0 auto 8px;
            font-size: 20px;
            position: relative;
            transition: all 0.3s;
        }

        .stage-icon.complete {
            background: var(--low);
            border-color: var(--low);
            color: var(--dark-primary);
        }

        .stage-icon.active {
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
            border-color: var(--quantum-purple);
            animation: stagePulse 2s infinite;
        }

        @keyframes stagePulse {
            0%, 100% { transform: scale(1); box-shadow: 0 0 0 0 rgba(0, 168, 255, 0.5); }
            50% { transform: scale(1.1); box-shadow: 0 0 20px 10px rgba(0, 168, 255, 0); }
        }

        .stage-label {
            font-size: 11px;
            color: var(--text-secondary);
            margin-bottom: 4px;
        }

        .stage-meta {
            font-size: 10px;
            color: var(--quantum-blue);
        }

        /* Attack Pattern Matrix (MITRE ATT&CK) */
        .attack-matrix {
            background: var(--glass);
            border: 1px solid var(--glass-border);
            border-radius: 16px;
            padding: 20px;
            margin-bottom: 25px;
        }

        .matrix-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 20px;
        }

        .matrix-grid {
            display: grid;
            grid-template-columns: repeat(14, 1fr);
            gap: 4px;
        }

        .tactic-column {
            background: var(--dark-tertiary);
