        .graph-btn.active {
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
        }

        #threatGraph {
            width: 100%;
            height: calc(100% - 60px);
            position: relative;
        }

        /* Detection Pipeline Visualization */
        .detection-pipeline {
            background: var(--glass);
            border: 1px solid var(--glass-border);
            border-radius: 16px;
            padding: 20px;
            margin-bottom: 25px;
        }

        .pipeline-stages {
            display: flex;
            justify-content: space-between;
            align-items: center;
            position: relative;
            padding: 20px 0;
        }

        .pipeline-connector {
            position: absolute;
            top: 50%;
            left: 0;
            right: 0;
            height: 2px;
            background: linear-gradient(90deg,
                var(--low) 0%,
                var(--quantum-blue) 50%,
                var(--glass-border) 100%);
            z-index: 1;
        }

        .pipeline-stage {
            flex: 1;
            text-align: center;
            position: relative;
            z-index: 2;
        }

        .stage-icon {
            width: 56px;
            height: 56px;
            background: var(--dark-tertiary);
            border: 2px solid var(--glass-border);
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            margin: 0 auto 8px;
            font-size: 20px;
            position: relative;
            transition: all 0.3s;
        }

        .stage-icon.complete {
            background: var(--low);
            border-color: var(--low);
            color: var(--dark-primary);
        }

        .stage-icon.active {
            background: linear-gradient(135deg, var(--quantum-blue), var(--quantum-purple));
            border-color: var(--quantum-purple);
            animation: stagePulse 2s infinite;
        }

        @keyframes stagePulse {
            0%, 100% { transform: scale(1); box-shadow: 0 0 0 0 rgba(0, 168, 255, 0.5); }
            50% { transform: scale(1.1); box-shadow: 0 0 20px 10px rgba(0, 168, 255, 0); }
        }

        .stage-label {
            font-size: 11px;
            color: var(--text-secondary);
            margin-bottom: 4px;
        }

        .stage-meta {
            font-size: 10px;
            color: var(--quantum-blue);
        }

        /* Attack Pattern Matrix (MITRE ATT&CK) */
        .attack-matrix {
            background: var(--glass);
            border: 1px solid var(--glass-border);
            border-radius: 16px;
            padding: 20px;
            margin-bottom: 25px;
        }

        .matrix-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 20px;
        }

        .matrix-grid {
            display: grid;
            grid-template-columns: repeat(14, 1fr);
            gap: 4px;
        }

        .tactic-column {
            background: var(--dark-tertiary);
            border-radius: 6px;
            padding: 8px 4px;
        }

        .tactic-header {
            font-size: 9px;
            font-weight: 600;
            text-align: center;
            color: var(--quantum-blue);
            margin-bottom: 6px;
            text-transform: uppercase;
            writing-mode: vertical-rl;
            text-orientation: mixed;
            height: 80px;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .technique-cell {
            background: var(--glass);
            border: 1px solid var(--glass-border);
            border-radius: 4px;
            padding: 4px;
            margin-bottom: 2px;
            font-size: 8px;
            text-align: center;
            cursor: pointer;
            transition: all 0.2s;
            position: relative;
            overflow: hidden;
        }

        .technique-cell:hover {
            background: var(--quantum-blue);
            transform: scale(1.1);
            z-index: 10;
        }

        .technique-cell.detected {
            background: rgba(132, 204, 22, 0.2);
            border-color: var(--low);
        }

        .technique-cell.partial {
            background: rgba(245, 158, 11, 0.2);
            border-color: var(--medium);
        }

        .technique-cell.critical {
            background: rgba(220, 38, 38, 0.2);
            border-color: var(--critical);
        }

        /* Live Threat Feed */
        /* Combined Right Panel - Threat Feed + Performance */
        .threat-feed {
            background: rgba(10, 10, 15, 0.95);
            backdrop-filter: blur(20px);
            border-left: 1px solid var(--glass-border);
            overflow-y: auto;
            padding: 20px;
            display: flex;
            flex-direction: column;
            gap: 20px;
        }

        .feed-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding-bottom: 15px;
            border-bottom: 1px solid var(--glass-border);
            margin-bottom: 15px;
        }

        .feed-title {
            font-size: 13px;
            font-weight: 600;
        }

        .feed-status {
            display: flex;
            align-items: center;
            gap: 6px;
            font-size: 10px;
            color: var(--low);
        }

        .feed-status-dot {
            width: 6px;
            height: 6px;
            background: var(--low);
            border-radius: 50%;
            animation: livePulse 2s infinite;
        }

        @keyframes livePulse {
            0%, 100% { opacity: 1; }
            50% { opacity: 0.3; }
        }

        .feed-item {
            background: var(--dark-tertiary);
            border-radius: 10px;
            padding: 12px;
            margin-bottom: 10px;
            border-left: 3px solid transparent;
            cursor: pointer;
            transition: all 0.2s;
            position: relative;
        }

        .feed-item:hover {
            transform: translateX(-5px);
            background: var(--dark-surface);
        }

        .feed-item.critical {
            border-left-color: var(--critical);
        }

        .feed-item.high {
            border-left-color: var(--high);
        }

        .feed-item.medium {
            border-left-color: var(--medium);
        }

