// ==================== ADVANCED THREAT GRAPH ENGINE ====================
let graphData = null;
let graphSimulation = null;
let graphAnimationFrame = null;
let activeFilters = new Set(['actors', 'malware', 'vulnerabilities', 'techniques', 'campaigns']);

function initializeAdvancedThreatGraph() {
    // Generate comprehensive threat intelligence data
    graphData = generateThreatIntelligenceData();

    // Initialize D3.js force simulation
    const svg = d3.select('#threatGraphSVG');
    const width = svg.node().getBoundingClientRect().width;
    const height = svg.node().getBoundingClientRect().height;

    // Create force simulation
    graphSimulation = d3.forceSimulation(graphData.nodes)
        .force('link', d3.forceLink(graphData.links).id(d => d.id).distance(80))
        .force('charge', d3.forceManyBody().strength(-300))
        .force('center', d3.forceCenter(width / 2, height / 2))
        .force('collision', d3.forceCollide().radius(d => d.radius + 5));

    // Create link elements
    const links = svg.append('g')
        .attr('class', 'links')
        .selectAll('line')
        .data(graphData.links)
        .enter().append('line')
        .attr('stroke', d => getEdgeColor(d.type))
        .attr('stroke-width', d => d.strength * 2)
        .attr('opacity', 0.6);

    // Create node groups
    const nodes = svg.append('g')
        .attr('class', 'nodes')
        .selectAll('g')
        .data(graphData.nodes)
        .enter().append('g')
        .call(d3.drag()
            .on('start', dragStarted)
            .on('drag', dragged)
            .on('end', dragEnded));

    // Add circles for nodes
    nodes.append('circle')
        .attr('r', d => d.radius)
        .attr('fill', d => getNodeColor(d.type))
        .attr('stroke', '#fff')
        .attr('stroke-width', 2)
        .style('cursor', 'pointer')
        .on('click', showNodeDetails)
        .on('mouseover', highlightConnections)
        .on('mouseout', resetHighlight);

    // Add icons/text for nodes
    nodes.append('text')
        .attr('text-anchor', 'middle')
        .attr('dy', '.35em')
        .attr('font-size', d => d.radius > 20 ? '16px' : '12px')
        .attr('fill', 'white')
        .text(d => d.icon);

    // Add labels
    nodes.append('text')
        .attr('x', d => d.radius + 5)
        .attr('y', 3)
        .attr('font-size', '10px')
        .attr('fill', '#94f3e4')
        .text(d => d.label);

    // Update positions on tick
    graphSimulation.on('tick', () => {
        links
            .attr('x1', d => d.source.x)
            .attr('y1', d => d.source.y)
            .attr('x2', d => d.target.x)
            .attr('y2', d => d.target.y);

        nodes.attr('transform', d => `translate(${d.x}, ${d.y})`);
    });

    // Update statistics
    updateGraphStatistics();

    // Start animation
    startGraphAnimation();
}

