        {source: 'cobalt_strike', target: 't1055', type: 'implements', strength: 2},
        {source: 'mimikatz', target: 't1003', type: 'performs', strength: 3},
        {source: 'emotet', target: 't1486', type: 'leads_to', strength: 2},

        // Vulnerability -> Campaign
        {source: 'zerologon', target: 'hafnium', type: 'exploited_in', strength: 3},
        {source: 'cve_2024_1234', target: 'solarwinds', type: 'exploited', strength: 2},

        // Technique chains
