}

function getNodeColor(type) {
    const colors = {
        actor: 'linear-gradient(135deg, #7c3aed, #a855f7)',
        malware: 'linear-gradient(135deg, #dc2626, #ef4444)', 
        vulnerability: 'linear-gradient(135deg, #f59e0b, #fbbf24)',
        technique: 'linear-gradient(135deg, #3b82f6, #60a5fa)',
        campaign: 'linear-gradient(135deg, #10b981, #34d399)'
    };
    return colors[type] || '#6b7280';
}

function getEdgeColor(type) {
    const colors = {
        uses: '#7c3aed',
        deploys: '#dc2626',
        attributed: '#10b981',
        implements: '#3b82f6',
        exploited: '#f59e0b',
        precedes: '#6b7280'
    };
    return colors[type] || '#4b5563';
}

function dragStarted(event, d) {
    if (!event.active) graphSimulation.alphaTarget(0.3).restart();
    d.fx = d.x;
    d.fy = d.y;
}

function dragged(event, d) {
    d.fx = event.x;
    d.fy = event.y;
}

function dragEnded(event, d) {
    if (!event.active) graphSimulation.alphaTarget(0);
    d.fx = null;
    d.fy = null;
}

function showNodeDetails(event, d) {
    const panel = document.getElementById('nodeDetailsPanel');
    const details = document.getElementById('nodeDetails');

    details.innerHTML = `
        <div style="background: rgba(0,0,0,0.5); padding: 10px; border-radius: 8px; margin-bottom: 10px;">
            <div style="font-size: 24px; margin-bottom: 10px;">${d.icon}</div>
            <h4 style="color: #fff; margin: 0;">${d.label}</h4>
            <div style="color: #9ca3af; font-size: 12px; margin-top: 5px;">Type: ${d.type}</div>
        </div>

        <div style="margin-bottom: 15px;">
            <div style="color: #9ca3af; font-size: 12px; margin-bottom: 5px;">Risk Score</div>
            <div style="background: #1f2937; border-radius: 4px; overflow: hidden;">
                <div style="background: ${d.risk > 80 ? '#dc2626' : d.risk > 60 ? '#f59e0b' : '#10b981'}; 
                            width: ${d.risk}%; padding: 4px 8px; color: white; font-size: 12px;">
                    ${d.risk}%
                </div>
            </div>
        </div>

        <div style="margin-bottom: 15px;">
            <div style="color: #9ca3af; font-size: 12px; margin-bottom: 8px;">Connections</div>
            ${graphData.links
                .filter(l => l.source.id === d.id || l.target.id === d.id)
                .map(l => `
                    <div style="background: rgba(0,0,0,0.3); padding: 6px 8px; border-radius: 4px; margin-bottom: 4px; font-size: 11px;">
                        <span style="color: #94f3e4;">${l.source.id === d.id ? l.target.label : l.source.label}</span>
                        <span style="color: #6b7280;"> (${l.type})</span>
                    </div>
                `).join('')}
        </div>

        <button onclick="investigateNode('${d.id}')" style="width: 100%; background: linear-gradient(135deg, #7c3aed, #a855f7); 
                border: none; color: white; padding: 8px; border-radius: 6px; cursor: pointer;">
            🔍 Investigate
        </button>
    `;

    panel.style.display = 'block';
}

function highlightConnections(event, d) {
    d3.selectAll('.links line')
        .style('opacity', l => (l.source.id === d.id || l.target.id === d.id) ? 1 : 0.2);

    d3.selectAll('.nodes g')
        .style('opacity', n => {
            const connected = graphData.links.some(l => 
                (l.source.id === d.id && l.target.id === n.id) ||
                (l.target.id === d.id && l.source.id === n.id) ||
                n.id === d.id
            );
            return connected ? 1 : 0.3;
        });
}

function resetHighlight() {
    d3.selectAll('.links line').style('opacity', 0.6);
    d3.selectAll('.nodes g').style('opacity', 1);
}

function updateGraphStatistics() {
    document.getElementById('nodeCount').textContent = graphData.nodes.length;
    document.getElementById('edgeCount').textContent = graphData.links.length;
    document.getElementById('clusterCount').textContent = detectClusters().length;
}

function detectClusters() {
    // Simple clustering detection based on connected components
    const clusters = [];
    const visited = new Set();

    graphData.nodes.forEach(node => {
        if (!visited.has(node.id)) {
            const cluster = [];
            const queue = [node];

            while (queue.length > 0) {
                const current = queue.shift();
                if (!visited.has(current.id)) {
                    visited.add(current.id);
                    cluster.push(current);

                    graphData.links.forEach(link => {
                        if (link.source.id === current.id && !visited.has(link.target.id)) {
                            queue.push(link.target);
                        }
                        if (link.target.id === current.id && !visited.has(link.source.id)) {
                            queue.push(link.source);
                        }
                    });
                }
            }

            if (cluster.length > 0) clusters.push(cluster);
        }
    });

    return clusters;
}

function toggleFilter(element, filterType) {
    element.classList.toggle('active');

    if (activeFilters.has(filterType)) {
        activeFilters.delete(filterType);
    } else {
        activeFilters.add(filterType);
    }

    updateGraphVisualization();
}

function updateGraphVisualization() {
    const filteredNodes = graphData.nodes.filter(n => activeFilters.has(n.type));
    const filteredNodeIds = new Set(filteredNodes.map(n => n.id));
    const filteredLinks = graphData.links.filter(l => 
        filteredNodeIds.has(l.source.id) && filteredNodeIds.has(l.target.id)
    );

    // Update D3 visualization with filtered data
