function toggleGraphAnimation() {
    if (graphAnimationFrame) {
        cancelAnimationFrame(graphAnimationFrame);
        graphAnimationFrame = null;
        event.target.innerHTML = '▶️ Play';
    } else {
        startGraphAnimation();
        event.target.innerHTML = '⏸️ Pause';
    }
}

function startGraphAnimation() {
    // Add subtle pulsing effect to high-risk nodes
    function animate() {
        const time = Date.now() * 0.001;

        d3.selectAll('.nodes circle')
            .attr('r', d => d.radius + Math.sin(time * 2) * (d.risk / 100) * 3)
            .attr('opacity', d => 0.8 + Math.sin(time * 3) * 0.2 * (d.risk / 100));

        graphAnimationFrame = requestAnimationFrame(animate);
    }
    animate();
}

function resetGraphView() {
    graphSimulation.alpha(1).restart();

    const svg = d3.select('#threatGraphSVG');
    const width = svg.node().getBoundingClientRect().width;
    const height = svg.node().getBoundingClientRect().height;

    graphSimulation.force('center', d3.forceCenter(width / 2, height / 2));

    showNotification('Graph view reset', 'success');
}

function exportGraphData() {
    const exportData = {
        timestamp: new Date().toISOString(),
        nodes: graphData.nodes.map(n => ({
            id: n.id,
