            totalEdges: graphData.links.length,
            avgRisk: graphData.nodes.reduce((sum, n) => sum + n.risk, 0) / graphData.nodes.length
        }
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], {type: 'application/json'});
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `threat-graph-${Date.now()}.json`;
    a.click();

    showNotification('Graph data exported successfully', 'success');
}

