function updateGraphMode() {
    const mode = document.getElementById('graphMode').value;
    showNotification(`Switching to ${mode} layout`, 'info');

    if (!graphSimulation || !graphData) {
        console.error('Graph not initialized');
        return;
    }

    const svg = d3.select('#threatGraphSVG');
    const width = svg.node().getBoundingClientRect().width;
    const height = svg.node().getBoundingClientRect().height;

    // Stop current simulation
    graphSimulation.stop();

    // Apply different layout algorithms
    switch(mode) {
        case 'hierarchical':
            applyHierarchicalLayout(width, height);
            break;
        case 'circular':
            applyCircularLayout(width, height);
            break;
        case 'timeline':
            applyTimelineLayout(width, height);
            break;
        default:
            applyForceLayout(width, height);
            break;
    }
}

function applyForceLayout(width, height) {
    // Reset to force-directed layout
    graphSimulation = d3.forceSimulation(graphData.nodes)
        .force('link', d3.forceLink(graphData.links).id(d => d.id).distance(80))
        .force('charge', d3.forceManyBody().strength(-300))
        .force('center', d3.forceCenter(width / 2, height / 2))
        .force('collision', d3.forceCollide().radius(d => d.radius + 5))
        .on('tick', updateNodePositions);

    graphSimulation.alpha(1).restart();
}

function applyHierarchicalLayout(width, height) {
    // Create hierarchical structure
    const hierarchy = {
        name: 'root',
        children: [
            {
                name: 'Campaigns',
                children: graphData.nodes.filter(n => n.type === 'campaign').map(n => ({name: n.id, node: n}))
            },
            {
                name: 'Actors',
                children: graphData.nodes.filter(n => n.type === 'actor').map(n => ({name: n.id, node: n}))
            },
            {
                name: 'Malware',
                children: graphData.nodes.filter(n => n.type === 'malware').map(n => ({name: n.id, node: n}))
            },
            {
                name: 'Vulnerabilities',
                children: graphData.nodes.filter(n => n.type === 'vulnerability').map(n => ({name: n.id, node: n}))
            },
            {
                name: 'Techniques',
                children: graphData.nodes.filter(n => n.type === 'technique').map(n => ({name: n.id, node: n}))
            }
        ]
    };

    const root = d3.hierarchy(hierarchy);
    const treeLayout = d3.tree().size([width - 100, height - 100]);
    treeLayout(root);

    // Apply positions to original nodes
    root.descendants().forEach(d => {
        if (d.data.node) {
            d.data.node.x = d.x + 50;
            d.data.node.y = d.y + 50;
            d.data.node.fx = d.data.node.x;
            d.data.node.fy = d.data.node.y;
        }
    });

    updateNodePositions();
}

function applyCircularLayout(width, height) {
    const centerX = width / 2;
    const centerY = height / 2;
    const radius = Math.min(width, height) * 0.35;

    // Group nodes by type
    const groups = {
        actor: graphData.nodes.filter(n => n.type === 'actor'),
        malware: graphData.nodes.filter(n => n.type === 'malware'),
        vulnerability: graphData.nodes.filter(n => n.type === 'vulnerability'),
        technique: graphData.nodes.filter(n => n.type === 'technique'),
        campaign: graphData.nodes.filter(n => n.type === 'campaign')
    };

    let currentAngle = 0;
    const typeCount = Object.keys(groups).length;

    Object.entries(groups).forEach(([type, nodes], groupIndex) => {
        const groupAngleStart = (groupIndex * 2 * Math.PI) / typeCount;
        const groupAngleEnd = ((groupIndex + 1) * 2 * Math.PI) / typeCount;
        const angleStep = (groupAngleEnd - groupAngleStart) / (nodes.length + 1);

        nodes.forEach((node, i) => {
            const angle = groupAngleStart + angleStep * (i + 1);
            node.x = centerX + radius * Math.cos(angle);
            node.y = centerY + radius * Math.sin(angle);
            node.fx = node.x;
            node.fy = node.y;
        });
    });

    updateNodePositions();
}

function applyTimelineLayout(width, height) {
    // Sort nodes by risk score (as proxy for timeline)
    const sortedNodes = [...graphData.nodes].sort((a, b) => a.risk - b.risk);

    const padding = 50;
    const usableWidth = width - padding * 2;
    const usableHeight = height - padding * 2;

    // Create timeline bands for different types
    const bands = {
        campaign: 0.1,
        actor: 0.3,
        malware: 0.5,
        vulnerability: 0.7,
        technique: 0.9
    };

    sortedNodes.forEach((node, i) => {
        // X position based on index (time)
        node.x = padding + (i / (sortedNodes.length - 1)) * usableWidth;

        // Y position based on type
        const bandY = bands[node.type] || 0.5;
        node.y = padding + bandY * usableHeight;

        // Add some randomness to prevent overlap
        node.y += (Math.random() - 0.5) * 30;

        node.fx = node.x;
        node.fy = node.y;
    });

    updateNodePositions();
}

function updateNodePositions() {
    // Update link positions
    d3.selectAll('.links line')
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);

    // Update node positions
    d3.selectAll('.nodes g')
        .attr('transform', d => `translate(${d.x}, ${d.y})`);
}
