#!/usr/bin/env node
// Node.js로 만든 안정적인 Claude 실행기

const { spawn } = require('child_process');
const path = require('path');

const NODE_PATH = 'K:/PortableApps/tools/nodejs/node.exe';
const CLAUDE_CLI = 'K:/PortableApps/Claude-Code/node_modules/@anthropic-ai/claude-code/cli.js';

// 환경 변수 설정
const env = {
    ...process.env,
    CLAUDE_HOME: 'K:/PortableApps/Claude-Code',
    CLAUDE_CONFIG_FILE: 'K:/PortableApps/Claude-Code/.claude.json',
    PATH: `K:\\PortableApps\\tools\\nodejs;${process.env.PATH}`
};

// Claude 실행
const args = process.argv.slice(2);
const claude = spawn(NODE_PATH, [CLAUDE_CLI, ...args], {
    env,
    stdio: 'inherit',
    shell: false  // shell 사용 안함 = 더 안정적
});

claude.on('error', (err) => {
    console.error('실행 실패:', err);
    process.exit(1);
});

claude.on('exit', (code) => {
    process.exit(code || 0);
});

