#!/usr/bin/env node
/**
 * Shrimp Task Formatter
 * Shrimp Taskì˜ ì¶œë ¥ì„ ê¹”ë”í•˜ê²Œ í¬ë§·íŒ…í•˜ëŠ” ìœ í‹¸ë¦¬í‹°
 */

const fs = require('fs');
const path = require('path');

class ShrimpFormatter {
    constructor() {
        this.taskFile = 'K:/PortableApps/Claude-Code/ShrimpData/current-tasks.json';
        this.icons = {
            pending: 'â³',
            in_progress: 'ğŸ”„',
            completed: 'âœ…',
            blocked: 'ğŸš«',
            high: 'ğŸ”´',
            medium: 'ğŸŸ¡',
            low: 'ğŸŸ¢'
        };
    }

    loadTasks() {
        try {
            if (fs.existsSync(this.taskFile)) {
                const data = fs.readFileSync(this.taskFile, 'utf8');
                return JSON.parse(data);
            }
        } catch (error) {
            console.error('âŒ Task íŒŒì¼ ì½ê¸° ì‹¤íŒ¨:', error.message);
        }
        return { tasks: [] };
    }

    formatTask(task, index) {
        const icon = this.icons[task.status] || 'ğŸ“‹';
        const priority = this.icons[task.priority] || '';

        let output = `\n${icon} **[${index + 1}] ${task.name}**\n`;

        // ê°„ë‹¨í•œ ì„¤ëª…
        if (task.description) {
            output += `   ğŸ“ ${task.description.substring(0, 100)}${task.description.length > 100 ? '...' : ''}\n`;
        }

        // ìƒíƒœì™€ ìš°ì„ ìˆœìœ„
        output += `   ìƒíƒœ: ${this.translateStatus(task.status)}`;
        if (task.priority) {
            output += ` | ìš°ì„ ìˆœìœ„: ${priority} ${this.translatePriority(task.priority)}`;
        }
        output += '\n';

        // ì˜ì¡´ì„±
        if (task.dependencies && task.dependencies.length > 0) {
            output += `   ğŸ”— ì˜ì¡´: ${task.dependencies.length}ê°œ ì‘ì—…\n`;
        }

        // ì™„ë£Œ ìš”ì•½
        if (task.status === 'completed' && task.summary) {
            output += `   âœ¨ ${task.summary.substring(0, 80)}${task.summary.length > 80 ? '...' : ''}\n`;
        }

        return output;
    }

    translateStatus(status) {
        const translations = {
            'pending': 'ëŒ€ê¸°ì¤‘',
            'in_progress': 'ì§„í–‰ì¤‘',
            'completed': 'ì™„ë£Œ',
            'blocked': 'ì°¨ë‹¨ë¨'
        };
        return translations[status] || status;
    }

    translatePriority(priority) {
        const translations = {
            'high': 'ë†’ìŒ',
            'medium': 'ë³´í†µ',
            'low': 'ë‚®ìŒ'
        };
        return translations[priority] || priority;
    }

    displayDashboard() {
        const data = this.loadTasks();
        const tasks = data.tasks || [];

        if (tasks.length === 0) {
            console.log('\nğŸ“­ í˜„ì¬ ë“±ë¡ëœ ì‘ì—…ì´ ì—†ìŠµë‹ˆë‹¤.\n');
            return;
        }

        // ìƒíƒœë³„ ì¹´ìš´íŠ¸
        const counts = {
            pending: 0,
            in_progress: 0,
            completed: 0,
            blocked: 0
        };

        tasks.forEach(task => {
            if (counts.hasOwnProperty(task.status)) {
                counts[task.status]++;
            }
        });

        // ëŒ€ì‹œë³´ë“œ í—¤ë”
        console.log('\n' + '='.repeat(60));
        console.log('ğŸ“Š **Shrimp Task ëŒ€ì‹œë³´ë“œ**');
        console.log('='.repeat(60));

        // ìƒíƒœ ìš”ì•½
        console.log('\nğŸ“ˆ **ì‘ì—… í˜„í™©**');
        console.log(`   â³ ëŒ€ê¸°ì¤‘: ${counts.pending}ê°œ`);
        console.log(`   ğŸ”„ ì§„í–‰ì¤‘: ${counts.in_progress}ê°œ`);
        console.log(`   âœ… ì™„ë£Œ: ${counts.completed}ê°œ`);
        console.log(`   ğŸš« ì°¨ë‹¨: ${counts.blocked}ê°œ`);
        console.log(`   ğŸ“‹ ì „ì²´: ${tasks.length}ê°œ`);

        // ì§„í–‰ì¤‘ì¸ ì‘ì—…
        const inProgress = tasks.filter(t => t.status === 'in_progress');
        if (inProgress.length > 0) {
            console.log('\nğŸ”„ **í˜„ì¬ ì§„í–‰ì¤‘**');
            inProgress.forEach((task, i) => console.log(this.formatTask(task, i)));
        }

        // ëŒ€ê¸°ì¤‘ì¸ ì‘ì—…
        const pending = tasks.filter(t => t.status === 'pending');
        if (pending.length > 0) {
            console.log('\nâ³ **ëŒ€ê¸°ì¤‘ ì‘ì—…**');
            pending.forEach((task, i) => console.log(this.formatTask(task, i)));
        }

        // ìµœê·¼ ì™„ë£Œ ì‘ì—… (ìµœëŒ€ 3ê°œ)
        const completed = tasks.filter(t => t.status === 'completed')
            .sort((a, b) => new Date(b.updatedAt) - new Date(a.updatedAt))
            .slice(0, 3);

        if (completed.length > 0) {
            console.log('\nâœ… **ìµœê·¼ ì™„ë£Œ**');
            completed.forEach((task, i) => console.log(this.formatTask(task, i)));
        }

        console.log('\n' + '='.repeat(60) + '\n');
    }

    // ê°„ë‹¨í•œ ë¦¬ìŠ¤íŠ¸ í‘œì‹œ
    displaySimpleList() {
        const data = this.loadTasks();
        const tasks = data.tasks || [];

        if (tasks.length === 0) {
            console.log('ğŸ“­ ì‘ì—… ì—†ìŒ');
            return;
        }

        console.log('\nğŸ“‹ **ì‘ì—… ëª©ë¡**\n');
        tasks.forEach((task, index) => {
            const icon = this.icons[task.status] || 'ğŸ“‹';
            console.log(`${icon} [${index + 1}] ${task.name} (${this.translateStatus(task.status)})`);
        });
        console.log();
    }
}

// ì‹¤í–‰
if (require.main === module) {
    const formatter = new ShrimpFormatter();
    const args = process.argv.slice(2);

    if (args.includes('--simple') || args.includes('-s')) {
        formatter.displaySimpleList();
    } else {
        formatter.displayDashboard();
    }
}

module.exports = ShrimpFormatter;

