#!/usr/bin/env python3
"""
Shrimp Task ê¹”ë”í•œ ë·°ì–´ (Python ë²„ì „)
"""

import json
import os
import sys
from pathlib import Path

class TaskViewer:
    def __init__(self):
        self.task_file = Path("K:/PortableApps/Claude-Code/ShrimpData/current-tasks.json")
        self.icons = {
            'completed': 'âœ…',
            'in-progress': 'ğŸ”„',
            'pending': 'â³',
            'blocked': 'ğŸš«'
        }

    def load_tasks(self):
        """ì‘ì—… íŒŒì¼ ë¡œë“œ"""
        if not self.task_file.exists():
            return None

        try:
            with open(self.task_file, 'r', encoding='utf-8') as f:
                return json.load(f)
        except Exception as e:
            print(f"âŒ íŒŒì¼ ì½ê¸° ì˜¤ë¥˜: {e}")
            return None

    def display_clean(self):
        """ê¹”ë”í•œ ëŒ€ì‹œë³´ë“œ í‘œì‹œ"""
        data = self.load_tasks()
        if not data or 'tasks' not in data:
            print("\nğŸ“­ ë“±ë¡ëœ ì‘ì—…ì´ ì—†ìŠµë‹ˆë‹¤.\n")
            return

        tasks = data['tasks']

        # í—¤ë”
        print("\n" + "="*60)
        print("ğŸ“Š ì‘ì—… ëŒ€ì‹œë³´ë“œ")
        print("="*60)

        # ìƒíƒœë³„ ë¶„ë¥˜
        status_groups = {
            'in-progress': [],
            'pending': [],
            'completed': []
        }

        for task in tasks:
            status = task.get('status', 'pending')
            if status in status_groups:
                status_groups[status].append(task)

            # ì„œë¸ŒíƒœìŠ¤í¬ë„ ì²˜ë¦¬
            if 'subtasks' in task:
                for subtask in task['subtasks']:
                    sub_status = subtask.get('status', 'pending')
                    if sub_status in status_groups:
                        subtask['parent'] = task.get('title', task.get('id'))
                        status_groups[sub_status].append(subtask)

        # ì§„í–‰ì¤‘ í‘œì‹œ
        if status_groups['in-progress']:
            print("\nğŸ”„ ì§„í–‰ì¤‘ ì‘ì—…")
            for task in status_groups['in-progress']:
                title = task.get('title', task.get('name', task.get('id', 'ì œëª©ì—†ìŒ')))
                if 'parent' in task:
                    print(f"  â””â”€ {title}")
                else:
                    print(f"  â€¢ {title}")

                # ìƒì„¸ ì •ë³´
                if 'note' in task or 'details' in task:
                    note = task.get('note', task.get('details', ''))
                    if note and len(note) < 100:
                        print(f"    ğŸ’¡ {note}")

        # ëŒ€ê¸°ì¤‘ í‘œì‹œ
        if status_groups['pending']:
            print("\nâ³ ëŒ€ê¸°ì¤‘ ì‘ì—…")
            count = 0
            for task in status_groups['pending'][:10]:  # ìµœëŒ€ 10ê°œ
                title = task.get('title', task.get('name', task.get('id', 'ì œëª©ì—†ìŒ')))
                if 'parent' in task:
                    print(f"  â””â”€ {title}")
                else:
                    print(f"  â€¢ {title}")
                count += 1

            if len(status_groups['pending']) > 10:
                print(f"  ... ì™¸ {len(status_groups['pending'])-10}ê°œ")

        # ìµœê·¼ ì™„ë£Œ
        if status_groups['completed']:
            print("\nâœ… ìµœê·¼ ì™„ë£Œ")
            for task in status_groups['completed'][-5:]:  # ìµœê·¼ 5ê°œ
                title = task.get('title', task.get('name', task.get('id', 'ì œëª©ì—†ìŒ')))
                if 'parent' in task:
                    print(f"  â””â”€ {title}")
                else:
                    print(f"  â€¢ {title}")

        # í†µê³„
        print("\n" + "-"*60)
        total = len(tasks)
        print(f"ğŸ“ˆ ì „ì²´ {total}ê°œ ì‘ì—…")
        print(f"   ì§„í–‰: {len(status_groups['in-progress'])} | ", end="")
        print(f"ëŒ€ê¸°: {len(status_groups['pending'])} | ", end="")
        print(f"ì™„ë£Œ: {len(status_groups['completed'])}")
        print("="*60 + "\n")

    def display_simple(self):
        """ê°„ë‹¨ ëª©ë¡ í‘œì‹œ"""
        data = self.load_tasks()
        if not data or 'tasks' not in data:
            print("ğŸ“­ ì‘ì—… ì—†ìŒ")
            return

        print("\nğŸ“‹ ì‘ì—… ëª©ë¡\n")
        for i, task in enumerate(data['tasks'], 1):
            status = task.get('status', 'pending')
            icon = self.icons.get(status, 'ğŸ“‹')
            title = task.get('title', task.get('name', task.get('id', 'ì œëª©ì—†ìŒ')))
            print(f"{icon} [{i}] {title}")

            # ì„œë¸ŒíƒœìŠ¤í¬
            if 'subtasks' in task:
                for sub in task['subtasks']:
                    sub_status = sub.get('status', 'pending')
                    sub_icon = 'âœ“' if sub_status == 'completed' else 'Â·'
                    sub_title = sub.get('title', sub.get('name', 'ì„œë¸ŒíƒœìŠ¤í¬'))
                    print(f"    {sub_icon} {sub_title}")
        print()

def main():
    viewer = TaskViewer()

    if '--simple' in sys.argv or '-s' in sys.argv:
        viewer.display_simple()
    else:
        viewer.display_clean()

if __name__ == "__main__":
    main()

