#!/usr/bin/env python3
"""
Shrimp Task 깔끔한 뷰어 (Python 버전)
"""

import json
import os
import sys
from pathlib import Path

class TaskViewer:
    def __init__(self):
        self.task_file = Path("K:/PortableApps/Claude-Code/ShrimpData/current-tasks.json")
        self.icons = {
            'completed': '✅',
            'in-progress': '🔄',
            'pending': '⏳',
            'blocked': '🚫'
        }

    def load_tasks(self):
        """작업 파일 로드"""
        if not self.task_file.exists():
            return None

        try:
            with open(self.task_file, 'r', encoding='utf-8') as f:
                return json.load(f)
        except Exception as e:
            print(f"❌ 파일 읽기 오류: {e}")
            return None

    def display_clean(self):
        """깔끔한 대시보드 표시"""
        data = self.load_tasks()
        if not data or 'tasks' not in data:
            print("\n📭 등록된 작업이 없습니다.\n")
            return

        tasks = data['tasks']

        # 헤더
        print("\n" + "="*60)
        print("📊 작업 대시보드")
        print("="*60)

        # 상태별 분류
        status_groups = {
            'in-progress': [],
            'pending': [],
            'completed': []
        }

        for task in tasks:
            status = task.get('status', 'pending')
            if status in status_groups:
                status_groups[status].append(task)

            # 서브태스크도 처리
            if 'subtasks' in task:
                for subtask in task['subtasks']:
                    sub_status = subtask.get('status', 'pending')
                    if sub_status in status_groups:
                        subtask['parent'] = task.get('title', task.get('id'))
                        status_groups[sub_status].append(subtask)

        # 진행중 표시
        if status_groups['in-progress']:
            print("\n🔄 진행중 작업")
            for task in status_groups['in-progress']:
                title = task.get('title', task.get('name', task.get('id', '제목없음')))
                if 'parent' in task:
                    print(f"  └─ {title}")
                else:
                    print(f"  • {title}")

                # 상세 정보
                if 'note' in task or 'details' in task:
                    note = task.get('note', task.get('details', ''))
                    if note and len(note) < 100:
                        print(f"    💡 {note}")

        # 대기중 표시
        if status_groups['pending']:
            print("\n⏳ 대기중 작업")
            count = 0
            for task in status_groups['pending'][:10]:  # 최대 10개
                title = task.get('title', task.get('name', task.get('id', '제목없음')))
                if 'parent' in task:
                    print(f"  └─ {title}")
                else:
                    print(f"  • {title}")
                count += 1

            if len(status_groups['pending']) > 10:
                print(f"  ... 외 {len(status_groups['pending'])-10}개")

        # 최근 완료
        if status_groups['completed']:
            print("\n✅ 최근 완료")
            for task in status_groups['completed'][-5:]:  # 최근 5개
                title = task.get('title', task.get('name', task.get('id', '제목없음')))
                if 'parent' in task:
                    print(f"  └─ {title}")
                else:
                    print(f"  • {title}")

        # 통계
        print("\n" + "-"*60)
        total = len(tasks)
        print(f"📈 전체 {total}개 작업")
        print(f"   진행: {len(status_groups['in-progress'])} | ", end="")
        print(f"대기: {len(status_groups['pending'])} | ", end="")
        print(f"완료: {len(status_groups['completed'])}")
        print("="*60 + "\n")

    def display_simple(self):
        """간단 목록 표시"""
        data = self.load_tasks()
        if not data or 'tasks' not in data:
            print("📭 작업 없음")
            return

        print("\n📋 작업 목록\n")
        for i, task in enumerate(data['tasks'], 1):
            status = task.get('status', 'pending')
            icon = self.icons.get(status, '📋')
            title = task.get('title', task.get('name', task.get('id', '제목없음')))
            print(f"{icon} [{i}] {title}")

            # 서브태스크
            if 'subtasks' in task:
                for sub in task['subtasks']:
                    sub_status = sub.get('status', 'pending')
                    sub_icon = '✓' if sub_status == 'completed' else '·'
                    sub_title = sub.get('title', sub.get('name', '서브태스크'))
                    print(f"    {sub_icon} {sub_title}")
        print()

def main():
    viewer = TaskViewer()

    if '--simple' in sys.argv or '-s' in sys.argv:
        viewer.display_simple()
    else:
        viewer.display_clean()

if __name__ == "__main__":
    main()

