        // K:/ 직접 경로 시도
        const kPath = originalPath.replace('/k/', 'K:/');
        if (originalFS.existsSync(kPath)) {
            console.log(`Path converted: ${originalPath} -> ${kPath}`);
            return kPath;
        }
    }

    // /tmp/ 경로 처리
    if (originalPath.startsWith('/tmp/')) {
        const tmpPath = originalPath.replace('/tmp/', 'K:/PortableApps/Claude-Code/tmp/');
        console.log(`Tmp path converted: ${originalPath} -> ${tmpPath}`);
        return tmpPath;
    }

    return originalPath;
}

// fs 함수들 오버라이드
