@echo off
echo ===== POPUP TERMINAL MONITOR =====
echo Monitoring for fast popup terminals...
echo Press Ctrl+C to stop
echo.

:loop
for /f "tokens=1,2,3,4,5,6 delims= " %%a in ('wmic process where "CreationDate>'%date:~10,4%%date:~4,2%%date:~7,2%%time:~0,2%%time:~3,2%%time:~6,2%.000000-000'" get Name^,ProcessId^,CommandLine /format:list 2^>nul') do (
    echo [%date% %time%] NEW: %%a
)
timeout /t 1 /nobreak >nul
goto loop

# Popup Terminal Catcher
Write-Host "üîç POPUP TERMINAL CATCHER STARTED" -ForegroundColor Cyan
Write-Host "Monitoring for processes that spawn and close quickly..." -ForegroundColor Yellow
Write-Host ""

$suspiciousProcesses = @()
$knownPopupExes = @('cmd.exe', 'conhost.exe', 'powershell.exe', 'wscript.exe', 'cscript.exe')

# Monitor process creation events
Register-WmiEvent -Query "SELECT * FROM Win32_ProcessStartTrace" -Action {
    $process = $Event.SourceEventArgs.NewEvent
    $name = $process.ProcessName
    $pid = $process.ProcessID
    $time = Get-Date -Format "HH:mm:ss.fff"

    # Highlight suspicious processes
    if ($name -in $knownPopupExes) {
        Write-Host "‚ö†Ô∏è [$time] SUSPICIOUS: $name (PID: $pid)" -ForegroundColor Red -BackgroundColor Yellow

        # Try to get more info
        try {
            $fullProcess = Get-WmiObject Win32_Process -Filter "ProcessId = $pid"
            if ($fullProcess) {
                Write-Host "   Command: $($fullProcess.CommandLine)" -ForegroundColor Magenta
                Write-Host "   Path: $($fullProcess.ExecutablePath)" -ForegroundColor Cyan

                # Log to file
                "$time | $name | $pid | $($fullProcess.CommandLine)" | Out-File -Append "K:\PortableApps\Claude-Code\popup-log.txt"
            }
        } catch {}
    } else {
        Write-Host "[$time] Process: $name (PID: $pid)" -ForegroundColor Green
    }
}

Write-Host "Press Ctrl+C to stop monitoring..." -ForegroundColor Gray
while ($true) { Start-Sleep -Seconds 1 }

@echo off
echo ===== CHECKING SCHEDULED TASKS =====
echo.
echo [1] Tasks running in next 5 minutes:
schtasks /query /fo LIST /v | findstr /i "Next Run Time:"
echo.
echo [2] Recently triggered tasks:
schtasks /query /fo LIST /v | findstr /i "Last Run Time:"
echo.
echo [3] Tasks with CMD or PowerShell:
schtasks /query /fo LIST /v | findstr /i "cmd powershell wscript cscript"
echo.
echo [4] All enabled tasks:
echo.
powershell -Command "Get-ScheduledTask | Where-Object {$_.State -eq 'Ready'} | Select-Object TaskName, TaskPath, State | Format-Table -AutoSize"
pause

@echo off
title POPUP MONITOR - DO NOT CLOSE
color 0A
echo ===============================================
echo         POPUP TERMINAL MONITOR v2.0
echo ===============================================
echo.
echo Monitoring all new processes...
echo Focus-stealing processes will be highlighted!
echo.

:monitor
echo [%time%] Checking for new processes...

REM Check for common popup culprits
tasklist /fi "IMAGENAME eq cmd.exe" /fo csv | findstr /v "Image" > temp_cmd.txt
tasklist /fi "IMAGENAME eq conhost.exe" /fo csv | findstr /v "Image" > temp_con.txt
tasklist /fi "IMAGENAME eq powershell.exe" /fo csv | findstr /v "Image" > temp_ps.txt

REM Show any found
for /f "tokens=1,2 delims=," %%a in (temp_cmd.txt) do (
    echo    [!] CMD.EXE found: PID %%b
)
for /f "tokens=1,2 delims=," %%a in (temp_con.txt) do (
    echo    [!] CONHOST found: PID %%b
)
for /f "tokens=1,2 delims=," %%a in (temp_ps.txt) do (
    echo    [!] POWERSHELL found: PID %%b
)

del temp_*.txt 2>nul

timeout /t 2 /nobreak >nul
goto monitor

