                stats.totalHistoryItems += project.history.length;
                stats.projects.push({
                    path: projectPath,
                    historyCount: project.history.length
                });
            }
        }

        return stats;
    }
}

// Export for use
module.exports = ClaudeHistoryManager;

// CLI usage
if (require.main === module) {
    const manager = new ClaudeHistoryManager();

