    const args = process.argv.slice(2);
    const command = args[0] || 'analyze';

    switch (command) {
        case 'analyze':
            fixer.analyze();
            fixer.showSessions();
            console.log('\n[?] Run with "fix" to clean the file');
            break;

        case 'fix':
            const bugs = fixer.analyze();
            if (bugs.length > 0) {
                fixer.fix();
                fixer.showSessions();
            } else {
